-- Responsável por obter o nome do arquivo conforme padrão TISS
CREATE OR REPLACE FUNCTION pls_gerar_pre_estab_ans_pck.obter_nome_arquivo ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nr_seq_arquivo_p pls_monitor_tiss_arquivo.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


cd_ans_w		pls_monitor_tiss_lote.cd_ans%type;
dt_competencia_w	varchar(6);
qt_zero_w		varchar(6);
ds_retorno_w		pls_monitor_tiss_arquivo.nm_arquivo%type;

BEGIN

if (nr_seq_arquivo_p IS NOT NULL AND nr_seq_arquivo_p::text <> '') then

	select	cd_ans,
		replace(to_char(dt_mes_competencia, 'yyyy/mm'), '/','') mes_ano
	into STRICT	cd_ans_w,
		dt_competencia_w
	from	pls_monitor_tiss_lote
	where	nr_sequencia = nr_seq_lote_p;

	--Acrescenta '0' a esquerda da sequência da tabela, completando 4 digitos
	for	i in  1 .. (4 - length(nr_seq_arquivo_p)) loop
		qt_zero_w := qt_zero_w||'0';
	end loop;


	ds_retorno_w := cd_ans_w||dt_competencia_w||qt_zero_w||nr_seq_arquivo_p||'.XTE';
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
