CREATE OR REPLACE PROCEDURE pls_gerar_radar_tiss_pck.gerar_pergunta_radar_4 () AS $body$
DECLARE

	 
	ie_mensagem_w		varchar(5);
	ie_tipo_canal_w		varchar(5);
	cd_estabelecimento_w	bigint;
	cont_w			bigint;
	
	C01 CURSOR FOR 
		SELECT	VL_DOMINIO 
		from	valor_dominio 
		where	CD_DOMINIO = 5897;
		
	C02 CURSOR FOR 
		SELECT	VL_DOMINIO 
		from	valor_dominio 
		where	CD_DOMINIO = 5898;
	
	
BEGIN 
 
	select	cd_estabelecimento 
	into STRICT	cd_estabelecimento_w 
	from	pls_radar_tiss 
	where	nr_sequencia	= pls_gerar_radar_tiss_pck.get_nr_seq_radar();
 
	open C01;
	loop 
	fetch C01 into	 
		ie_mensagem_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
 
		open c02;
		loop 
		fetch c02 into	 
			ie_tipo_canal_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
 
			select	count(*) 
			into STRICT	cont_w 
			from	PLS_PARAMETROS_RADAR04 
			where	cd_estabelecimento	= cd_estabelecimento_w 
			and	IE_MENSAGEM		= ie_mensagem_w 
			and	IE_TIPO_CANAL		= ie_tipo_canal_w;
 
			insert into pls_radar_pergunta04(nr_sequencia, 
				dt_atualizacao, 
				nm_usuario, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec, 
				nr_seq_radar, 
				IE_MENSAGEM, 
				IE_TIPO_CANAL, 
				IE_ELEGIBILIDADE) 
			values (nextval('pls_radar_pergunta04_seq'), 
				clock_timestamp(), 
				get_nm_usuario, 
				clock_timestamp(), 
				get_nm_usuario, 
				get_nr_seq_radar, 
				ie_mensagem_w, 
				ie_tipo_canal_w, 
				CASE WHEN cont_w=0 THEN  'N'  ELSE 'S' END );
		end loop;
		close c02;
		 
		end;
	end loop;
	close C01;
	 
	end;
	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
