CREATE OR REPLACE PROCEDURE pls_gerar_radar_tiss_pck.gerar_pergunta_radar_6 () AS $body$
DECLARE

	 
	IE_FORMA_ACESSO_w		varchar(5);
	cont_w				bigint;
	qt_implantado_w			varchar(255);
	cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;
	
	C01 CURSOR FOR 
		SELECT	VL_DOMINIO 
		from	valor_dominio 
		where	CD_DOMINIO = 5899;
	
	
BEGIN 
	 
	select	cd_estabelecimento 
	into STRICT	cd_estabelecimento_w 
	from	pls_radar_tiss 
	where	nr_sequencia	= pls_gerar_radar_tiss_pck.get_nr_seq_radar();
	 
	open C01;
	loop 
	fetch C01 into	 
		IE_FORMA_ACESSO_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
 
		select	count(*), 
			sum(CASE WHEN x.ie_implantado='S' THEN  1  ELSE 0 END ) 
		into STRICT	cont_w, 
			qt_implantado_w 
		from	pls_prest_radar06	x, 
			pls_prestador		a 
		where	a.nr_sequencia		= x.nr_seq_prestador 
		and	a.ie_situacao		= 'A' 
		and	a.cd_estabelecimento	= cd_estabelecimento_w 
		and	a.ie_tipo_relacao 	<> 'F' 
		and	x.ie_forma_acesso	= ie_forma_acesso_w;
		 
 
		insert into pls_radar_pergunta06(nr_sequencia, 
				dt_atualizacao, 
				nm_usuario, 
				dt_atualizacao_nrec, 
				nm_usuario_nrec, 
				nr_seq_radar, 
				IE_FORMA_ACESSO, 
				IE_IMPLANTADO, 
				QT_PRESTADORES) 
		values (	nextval('pls_radar_pergunta06_seq'), 
				clock_timestamp(), 
				get_nm_usuario, 
				clock_timestamp(), 
				get_nm_usuario, 
				get_nr_seq_radar, 
				IE_FORMA_ACESSO_w, 
				CASE WHEN qt_implantado_w=0 THEN  'N'  ELSE 'S' END , 
				cont_w);
	end loop;
	close C01;
	 
	end;
	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
