CREATE OR REPLACE FUNCTION pls_gerencia_autogerado_pck.pls_obter_lote_autogerado ( dt_ref_w pls_lote_auto_gerado.dt_inicio%type, cd_prestador_w pls_conta_proc_v.cd_prestador_exec%type) RETURNS PLS_LOTE_AUTO_GERADO.NR_SEQUENCIA%TYPE AS $body$
DECLARE


nr_seq_lote_w	pls_lote_auto_gerado.nr_sequencia%type;												
	

BEGIN												
	nr_seq_lote_w := null;
	if (cd_prestador_w IS NOT NULL AND cd_prestador_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_lote_w
		from	pls_lote_auto_gerado a
		where	ie_status	= 'F'
		and		exists ( SELECT	1
						 from	pls_auto_ger_itens b
						 where	b.nr_seq_lote = a.nr_sequencia
						 and 	b.cd_prestador = cd_prestador_w)
		and		dt_inicio	= (	select	max(dt_inicio)
								from	pls_lote_auto_gerado c
								where	ie_status	= 'F'
								and		dt_inicio	< trunc(dt_ref_w,'month')
								and		exists ( select	1
												 from	pls_auto_ger_itens d
												 where	d.nr_seq_lote = c.nr_sequencia
												 and 	d.cd_prestador = cd_prestador_w));
	end if;
	
	if (coalesce(nr_seq_lote_w::text, '') = '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_lote_w
		from	pls_lote_auto_gerado
		where	ie_status	= 'F'
		and		dt_inicio	= (	SELECT	max(dt_inicio)
								from	pls_lote_auto_gerado
								where	ie_status	= 'F'
								and		dt_inicio	< trunc(dt_ref_w,'month'));
	end if;
	
return nr_seq_lote_w;
	
end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
