SET client_encoding TO 'UTF8';

-- Apagar a constraint informada, utilize esta procedure quando uma constraint não
-- for mais necessária.
CREATE OR REPLACE PROCEDURE pls_gerencia_bd_pck.drop_constraint (nm_tabela_p text, nm_constraint_p text, -- Informe true para deletar a integridade do dicionário de dados também
 ie_dic_p boolean default false, -- Informe true para deletar a integridade do banco também
 ie_bco_p boolean default false) AS $body$
DECLARE


qt_w	integer;


BEGIN

-- só deleta do dicionário caso for indicado.
if (ie_dic_p) then

	delete	FROM integridade_atributo
	where	nm_tabela			= nm_tabela_p
	and	nm_integridade_referencial 	= nm_constraint_p;

	delete	FROM integridade_referencial
	where	nm_tabela			= nm_tabela_p
	and	nm_integridade_referencial 	= nm_constraint_p;

	commit;
end if;

-- só dropa do banco caso for indicado.
if (ie_bco_p) then

	select	count(1)
	into STRICT	qt_w
	from	user_constraints
	where	table_name	= nm_tabela_p
	and	constraint_name	= nm_constraint_p;

	if (qt_w > 0) then

		EXECUTE 'alter table ' || nm_tabela_p || ' drop constraint ' || nm_constraint_p;
	end if;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
