/*PROCEDURE deverá controlar a ativação e inativação das ocorrências no sistema 
IE_OPCAO_P 
A = ATIVAR 
I = INATIVAR 
*/
 


CREATE OR REPLACE PROCEDURE pls_gerencia_dados_ocor_pck.controla_situacao_ocorrencia ( nr_seq_ocor_benef_p pls_ocorrencia_benef.nr_sequencia%type, ie_opcao_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE
				
 
-- todas as ocorrências que são combinadas 
C01 CURSOR( nr_seq_ocor_benef_p	pls_ocorrencia_benef.nr_sequencia%type) FOR 
	SELECT	a.nr_sequencia nr_seq_ocor_benef 
	from	pls_ocorrencia_benef a, 
		pls_ocorrencia b 
	where	a.nr_sequencia		= nr_seq_ocor_benef_p 
and  b.nr_sequencia  = a.nr_seq_ocorrencia 
and  b.ie_regra_combinada	= 'S';

BEGIN 
 
-- inativa o que retornar 
for r_C01_w in C01(nr_seq_ocor_benef_p) loop 
  CALL pls_gerencia_dados_ocor_pck.pls_gerencia_situacao_ocor( r_C01_w.nr_seq_ocor_benef, ie_opcao_p, nm_usuario_p,'S');
end loop;
 
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
