CREATE OR REPLACE PROCEDURE pls_gerencia_dados_ocor_pck.pls_atualizar_glosa_fat ( nr_seq_ocorrencia_benef_p pls_ocorrencia_benef.nr_sequencia%type, nr_seq_conta_glosa_p pls_conta_glosa.nr_sequencia%type, qt_glosa_p pls_ocorrencia_benef.qt_glosa_fat%type) AS $body$
BEGIN
 
if (qt_glosa_p	> 0) then 
	 
	if (nr_seq_ocorrencia_benef_p IS NOT NULL AND nr_seq_ocorrencia_benef_p::text <> '') then 
		update	pls_ocorrencia_benef 
		set	qt_glosa_fat	= qt_glosa_p 
		where	nr_sequencia	= nr_seq_ocorrencia_benef_p;
		 
		update	pls_conta_glosa 
		set 	qt_glosa_fat	= qt_glosa_p 
		where	nr_seq_ocorrencia_benef	= nr_seq_ocorrencia_benef_p;
			 
		update	pls_conta_glosa 
		set 	qt_glosa_fat	= qt_glosa_p 
		where	nr_sequencia 	=(	SELECT	max(nr_seq_glosa) 
						from	pls_ocorrencia_benef 
						where	nr_sequencia = nr_seq_ocorrencia_benef_p);
	else 
		update	pls_conta_glosa 
		set 	qt_glosa_fat = qt_glosa_p 
		where	nr_sequencia = nr_seq_conta_glosa_p;
	end if;	
end if;
 
END;

$body$
