SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_gerencia_dados_ocor_pck.pls_reconsistir_ocor ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type, nr_seq_proc_partic_p pls_proc_participante.nr_sequencia%type, cd_acao_analise_p pls_acao_analise.cd_acao%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

 
vl_glosa_w		pls_conta_proc_v.vl_glosa%type	:= 0;

BEGIN
 
--Quando da ação aceitar valor apresentado deverão ser inativadas todas as glosas e ocorrências vinculadas ao item 
if (cd_acao_analise_p	= 4) then 
	CALL pls_gerencia_dados_ocor_pck.pls_gerencia_situ_glo_ocor( 	nr_seq_conta_p,	nr_seq_conta_proc_p, nr_seq_conta_mat_p, nr_seq_proc_partic_p, 
					'I', nm_usuario_p);
else 
--Necessário ativar a glosa 1705 quando da ação ser diverente de aceitar valor apresentado e houver valor de glosa	 
	if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then 
		select	max(vl_glosa) 
		into STRICT	vl_glosa_w 
		from	pls_conta_proc_v 
		where	nr_sequencia	= nr_seq_conta_proc_p;
	elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then 
		select	max(vl_glosa) 
		into STRICT	vl_glosa_w 
		from	pls_conta_mat_v 
		where	nr_sequencia	= nr_seq_conta_mat_p;
	end if;
	CALL pls_conta_ac_valor_glosa(	nr_seq_conta_proc_p, nr_seq_conta_mat_p, vl_glosa_w, 
					'P', nm_usuario_p);
end if;
 
--Inativar ocorrências 
CALL pls_gerencia_dados_ocor_pck.inativar_ocor_combinadas(nr_seq_conta_p, nr_seq_conta_proc_p, nr_seq_conta_mat_p, 
			 nr_seq_proc_partic_p, cd_acao_analise_p ,nm_usuario_p);
--gerar novamente as ocorrencias 
CALL pls_oc_cta_gerar_combinada(	null, null, null, null, null, nr_seq_conta_p, nr_seq_conta_proc_p, 
				nr_seq_conta_mat_p, nr_seq_proc_partic_p, cd_acao_analise_p, null, 
				null, null, null, cd_estabelecimento_p, nm_usuario_p);
				 
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
