SET client_encoding TO 'UTF8';


-- Ajusta o valor de glosa dos itens para evitar valores negativos,

-- uma vez que no existe o campo "vl_glosa" na tabela "pls_monitor_tiss_mat"

-- para gerar o valor de glosa,  feito a subtrao entre o valor apresentado

-- e o valor pago, podendo assim ter valores negativos



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.ajusta_vl_glosa_mat_exc ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_seq_mat_w		pls_util_cta_pck.t_number_table;
j			integer := 0;

C01 CURSOR(nr_seq_lote_pc	pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia,
		b.vl_glosa
	from	pls_monitor_tiss_cta_val a,
		pls_monitor_tiss_mat_val b
	where	a.nr_sequencia = b.nr_seq_cta_val
	and	a.nr_seq_lote_monitor = nr_seq_lote_pc;

BEGIN

for r_C01_w in C01(nr_seq_lote_p) loop

	if (r_C01_w.vl_glosa < 0) then

		tb_nr_seq_mat_w(j) := r_C01_w.nr_sequencia;

		if (tb_nr_seq_mat_w.count > current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer) then
			forall i in tb_nr_seq_mat_w.first .. tb_nr_seq_mat_w.last
				update	pls_monitor_tiss_mat_val set
					vl_glosa = 0
				where	nr_sequencia = tb_nr_seq_mat_w(i);
			commit;

			j := 0;
			tb_nr_seq_mat_w.delete;
		else
			j := j + 1;
		end if;
	end if;
end loop;

if (tb_nr_seq_mat_w.count > 0) then
	forall i in tb_nr_seq_mat_w.first .. tb_nr_seq_mat_w.last
		update	pls_monitor_tiss_mat_val set
			vl_glosa = 0
		where	nr_sequencia = tb_nr_seq_mat_w(i);
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
