CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.atualiza_cd_guia_prestador (nr_seq_lote_monitor_p pls_monitor_tiss_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_cd_guia_w		pls_util_cta_pck.t_varchar2_table_20;
j			integer;

C01 CURSOR(nr_seq_lote_monitor_pc	pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT	a.cd_guia_prestador_ans,
		b.cd_guia_prestador,
		b.nr_sequencia nr_seq_cta_val
	from	pls_conta a,
		pls_monitor_tiss_cta_val b
	where	a.nr_sequencia = b.nr_seq_conta
	and	b.nr_seq_lote_monitor = nr_seq_lote_monitor_pc;

BEGIN
j := 0;
for r_C01_w in C01(nr_seq_lote_monitor_p) loop

	if (r_C01_w.cd_guia_prestador_ans <> r_C01_w.cd_guia_prestador) then

		tb_cd_guia_w(j) 	:= r_C01_w.cd_guia_prestador_ans;
		tb_nr_sequencia_w(j) 	:= r_C01_w.nr_seq_cta_val;

		if (j >= pls_util_pck.qt_registro_transacao_w) then
			forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
				update	pls_monitor_tiss_cta_val
				set	cd_guia_prestador = tb_cd_guia_w(i)
				where	nr_sequencia = tb_nr_sequencia_w(i);
			commit;

			tb_cd_guia_w.delete;
			tb_nr_sequencia_w.delete;
			j := 0;
		else
			j := j + 1;
		end if;
	end if;
end loop;

if (tb_nr_sequencia_w.count > 0) then
	forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
		update	pls_monitor_tiss_cta_val
		set	cd_guia_prestador = tb_cd_guia_w(i)
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end if;


END;

$body$
