CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.atualiza_guia_ref_internacao ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


nr_seq_processo_exec_w	pls_monitor_tiss_processo.nr_sequencia%type;
tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_cd_guia_ok_w		pls_util_cta_pck.t_varchar2_table_20;

C01 CURSOR(nr_seq_lote_pc	pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		(SELECT	max(x.cd_guia_ok)
		 from	pls_conta x
		 where	x.nr_sequencia = a.nr_seq_conta) cd_guia_ok
	from	pls_monitor_tiss_cta_val a
	where	a.nr_seq_lote_monitor = nr_seq_lote_pc
	and	exists (	select	1
			from	pls_monitor_tiss_inc_val y
			where	a.nr_sequencia = y.nr_seq_cta_val
			and	y.cd_inconsistencia = 'INC016');


BEGIN

-- grava o log de processo que sinaliza a ao que foi feita

nr_seq_processo_exec_w := pls_gerencia_envio_ans_pck.grava_processo(	nr_seq_lote_p, 10, nm_usuario_p, null, nr_seq_processo_exec_w);

Open C01(nr_seq_lote_p);
loop
	tb_nr_sequencia_w.delete;
	tb_cd_guia_ok_w.delete;

	fetch C01 bulk collect into tb_nr_sequencia_w, tb_cd_guia_ok_w
	limit current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer;

	exit when tb_nr_sequencia_w.count = 0;

	forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
		update	pls_monitor_tiss_cta_val
		set	cd_guia_principal = tb_cd_guia_ok_w(i)
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close C01;

end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
