SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.atualiza_valor_pago_proc_conta ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_seq_cta_val_w		pls_util_cta_pck.t_number_table;
tb_vl_liberado_w		pls_util_cta_pck.t_number_table;
vl_liberado_w			pls_monitor_tiss_proc_val.vl_liberado%type;
index_w				integer := 0;

C01 CURSOR FOR
	SELECT	x.nr_sequencia nr_seq_conta_val
	from	pls_monitor_tiss_cta_val	x
	where	x.nr_seq_lote_monitor	= nr_seq_lote_p
	and	x.vl_total_procedimento	> 0
	and	exists (	SELECT	1
				from	pls_monitor_tiss_proc_val	a
				where	a.nr_seq_cta_val		= x.nr_sequencia
				and	a.vl_liberado			> 0);
BEGIN

for r_c01_w in c01 loop

	select	sum(coalesce(b.vl_liberado,0))
	into STRICT	vl_liberado_w
	from	pls_monitor_tiss_proc_val	b,
		pls_conta_proc			a
	where	b.nr_seq_conta_proc		= a.nr_sequencia
	and	b.nr_seq_conta			= a.nr_seq_conta
	and	b.nr_seq_cta_val		= r_c01_w.nr_seq_conta_val
	and	a.ie_tipo_despesa		= '1'
	and	a.ie_status 			not in ('M','D');

	tb_seq_cta_val_w(index_w)	:= r_c01_w.nr_seq_conta_val;
	tb_vl_liberado_w(index_w)	:= coalesce(vl_liberado_w,0);
	index_w				:= index_w + 1;


	if (mod(index_w,1000) = 0) then
		forall i in tb_seq_cta_val_w.first..tb_seq_cta_val_w.last
			update	pls_monitor_tiss_cta_val
			set	vl_total_procedimento	= tb_vl_liberado_w(i)
			where	nr_sequencia		= tb_seq_cta_val_w(i);

		tb_seq_cta_val_w.delete;
		tb_vl_liberado_w.delete;
		index_w	:= 0;
		commit;
	end if;
end loop;

if (tb_seq_cta_val_w.count > 0) then
	forall i in tb_seq_cta_val_w.first..tb_seq_cta_val_w.last
		update	pls_monitor_tiss_cta_val
		set	vl_total_procedimento	= tb_vl_liberado_w(i)
		where	nr_sequencia		= tb_seq_cta_val_w(i);

	tb_seq_cta_val_w.delete;
	tb_vl_liberado_w.delete;
	index_w	:= 0;
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
