SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.atualiza_valores_pre_pagto (nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_seq_upd_w		pls_util_cta_pck.t_number_table;
tb_tipo_repasse_w	pls_util_cta_pck.t_varchar2_table_2;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
			coalesce(	(SELECT	max(ie_tipo_repasse)
				from	pls_segurado_repasse
				where	nr_seq_segurado		= b.nr_seq_segurado
				and		nr_seq_congenere 	= coalesce(b.nr_seq_congenere, c.nr_seq_congenere)
				and		dt_repasse			<= b.dt_atendimento_referencia
				and		((dt_fim_repasse  	>= b.dt_atendimento_referencia) or (coalesce(dt_fim_repasse::text, '') = ''))),'C' ) ie_tipo_repasse
	from	pls_monitor_tiss_cta_val a,
			pls_conta b,
			pls_protocolo_conta c
	where	a.nr_seq_conta = b.nr_sequencia
	and 	b.nr_seq_protocolo = c.nr_sequencia
	and 	b.ie_origem_conta = 'Z'
	and		nr_seq_lote_monitor = nr_seq_lote_p;
	

BEGIN

	open C01;
	loop
		fetch C01 bulk collect into 	tb_seq_upd_w, tb_tipo_repasse_w
		limit current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer;

		exit when tb_seq_upd_w.count = 0;

		forall i in tb_seq_upd_w.first .. tb_seq_upd_w.last
			update 	pls_monitor_tiss_cta_val set
					vl_cobranca_guia	= 0,
					vl_total_diaria		= 0,
					vl_total_fornecedor = 0,
					vl_total_glosa		= 0,
					vl_total_material	= 0,
					vl_total_medicamentos = 0,
					vl_total_opm		= 0,
					vl_total_pago		= 0,
					vl_total_procedimento = 0,
					vl_total_tabela_propria = 0,
					vl_total_taxa		= 0
			where	nr_sequencia = tb_seq_upd_w(i)
			and		tb_tipo_repasse_w(i) != 'C';
		commit;

		forall i in tb_seq_upd_w.first .. tb_seq_upd_w.last
			update 	pls_monitor_tiss_proc_val  set
					vl_glosa	= 0,
					vl_liberado = 0,
					vl_procedimento = 0
			where	nr_seq_cta_val = tb_seq_upd_w(i)
			and		tb_tipo_repasse_w(i) != 'C';
		commit;

		forall i in tb_seq_upd_w.first .. tb_seq_upd_w.last
			update 	pls_monitor_tiss_mat_val  set
					vl_glosa	= 0,
					vl_liberado = 0,
					vl_material = 0,
					vl_pago_fornecedor = 0
			where	nr_seq_cta_val = tb_seq_upd_w(i)
			and		tb_tipo_repasse_w(i) != 'C';
		commit;

		tb_seq_upd_w.delete;
		tb_tipo_repasse_w.delete;
	end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerencia_envio_ans_pck.atualiza_valores_pre_pagto (nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
