SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.atualiza_valores_totais_lote (nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_tipo_atual_p text) AS $body$
DECLARE


nr_seq_processo_w	pls_monitor_tempo_lote.nr_sequencia%type;
vl_cobranca_guia_w	pls_monitor_tiss_cta_val.vl_cobranca_guia%type;
vl_total_pago_w		pls_monitor_tiss_cta_val.vl_total_pago%type;
vl_total_copartic_w	pls_monitor_tiss_lote.vl_total_copart%type;
vl_total_glosa_w	pls_monitor_tiss_lote.vl_total_glosa%type;


BEGIN
if (ie_tipo_atual_p = 'A') then
	nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Atualizando os valores totais do lote', 'I', nm_usuario_p, nr_seq_processo_w);

	--Valor total de coparticipao considerando apenas contas que entraram no lote e no tenha mexclusivamente evento de pagamento

	select coalesce(sum(vl_coparticipacao),0)
	into STRICT	vl_total_copartic_w
	from   pls_conta_coparticipacao
	where nr_seq_conta in (   SELECT distinct x.nr_seq_conta
		from   pls_monitor_tiss_cta_val x,
			   pls_monitor_tiss_lote y,
			   pls_monitor_tiss_alt z
		where  x.nr_seq_lote_monitor = y.nr_sequencia
		and    x.nr_seq_conta = z.nr_seq_conta
		and    z.ie_tipo_evento = 'FC'
		and    x.nr_seq_lote_monitor = nr_seq_lote_p
		and    trunc(z.dt_evento,'MM') = trunc(y.dt_mes_competencia,'MM'))
	and    ie_status_mensalidade In ('L', 'G')
	and    ie_status_coparticipacao = 'S';

	--Valor total de glosa considerando apenas contas que entraram no lote e no tenha mexclusivamente evento de pagamento

	select 	coalesce(sum(vl_glosa),0)
	into STRICT	vl_total_glosa_w
	from   	pls_conta
	where 	nr_sequencia in (   SELECT distinct x.nr_seq_conta
        from   pls_monitor_tiss_cta_val x,
               pls_monitor_tiss_lote y,
               pls_monitor_tiss_alt z
        where  x.nr_seq_lote_monitor = y.nr_sequencia
        and    x.nr_seq_conta = z.nr_seq_conta
        and    z.ie_tipo_evento = 'FC'
        and    x.nr_seq_lote_monitor = nr_seq_lote_p
        and    trunc(z.dt_evento,'MM') = trunc(y.dt_mes_competencia,'MM'));

	select	coalesce(sum(vl_cobranca_guia),0),
		coalesce(sum(vl_total_pago),0)
	into STRICT	vl_cobranca_guia_w,
		vl_total_pago_w
	from	pls_monitor_tiss_cta_val
	where	nr_seq_lote_monitor = nr_seq_lote_p;

	update	pls_monitor_tiss_lote
	set		vl_total_cobranca = vl_cobranca_guia_w,
			vl_total_pago = vl_total_pago_w,
			vl_total_copart = vl_total_copartic_w,
			vl_total_glosa = vl_total_glosa_w
	where	nr_sequencia = nr_seq_lote_p;

	nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);

elsif (ie_tipo_atual_p = 'Z') then
	update	pls_monitor_tiss_lote
	set	vl_total_cobranca = 0,
		vl_total_pago = 0,
		vl_total_copart = 0,
		vl_total_glosa = 0
	where	nr_sequencia = nr_seq_lote_p;
end if;

end;

-- Obter a restrio dos filtros do lote de monitoramento


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
