			vl_material		= dados_mat_update_p.vl_material(i),
			qt_material		= dados_mat_update_p.qt_material(i),
			vl_liberado		= dados_mat_update_p.vl_liberado(i),
			vl_glosa		= dados_mat_update_p.vl_glosa(i),
			cd_tabela_ref		= dados_mat_update_p.cd_tabela_ref(i),
			ie_tipo_despesa 	= dados_mat_update_p.ie_tipo_despesa(i),
			nr_seq_pacote		= dados_mat_update_p.nr_seq_pacote(i),
			ie_item_atualizado	= dados_mat_update_p.ie_item_atualizado(i),
			nr_seq_prest_fornec	= dados_mat_update_p.nr_seq_prest_fornec(i),
			cd_cgc_fornecedor	= dados_mat_update_p.cd_cgc_fornecedor(i),
			cd_material_tuss	= dados_mat_update_p.cd_material_tuss(i),
			vl_coparticipacao	= dados_mat_update_p.vl_coparticipacao(i),
			nr_seq_regra_tab_ref	= dados_mat_update_p.nr_seq_regra_tab_ref(i),
			ie_origem_tab_ref	= dados_mat_update_p.ie_origem_tab_ref(i),
			nr_seq_regra_gpo_proc	= dados_mat_update_p.nr_seq_regra_gpo_proc(i),
			ie_origem_grupo_proc	= dados_mat_update_p.ie_origem_grupo_proc(i),
			cd_unidade_medida	=	dados_mat_update_p.cd_unidade_medida(i)
