SET client_encoding TO 'UTF8';


--Define quais registros correspondem a fornecimento direto na geracao do lote de exclusao e alteracao



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.definir_fornec_direto_exclusao ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	nr_sequencia,
		(	SELECT  coalesce(max(a.ie_fornec_direto),'N')
			from	pls_protocolo_conta a,
				pls_conta b
			where 	a.nr_sequencia = b.nr_seq_protocolo
			and 	b.nr_sequencia = x.nr_seq_conta
		) ie_fornec_direto
	from	pls_monitor_tiss_cta_val x
	where	nr_seq_lote_monitor = nr_seq_lote_p;
						
BEGIN

	for r_c01_w in C01 loop
		if ( r_c01_w.ie_fornec_direto = 'S') then
	
			update 	pls_monitor_tiss_cta_val
			set	ie_fornec_direto = 'S'
			where 	nr_sequencia = r_c01_w.nr_sequencia;
			commit;
		end if;
	end loop;	
	
						

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
