SET client_encoding TO 'UTF8';


-- Deleta contas que no possuem nenhum registro aceito na ANS, para tentar evitar a glosa de nmero de guia invlido



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.deletar_contas_nao_aceitas_exc ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
j			integer;

C01 CURSOR(nr_seq_lote_pc	pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia,
		(SELECT	count(1)
		 from	pls_monitor_tiss_alt b
		 where	b.nr_seq_conta = a.nr_seq_conta
		 and	b.ie_status = 'A') qt_aceito
	from	pls_monitor_tiss_cta_val a
	where	a.nr_seq_lote_monitor = nr_seq_lote_pc;

BEGIN
tb_nr_sequencia_w.delete;
j := 0;

for r_C01_w in C01(nr_seq_lote_p) loop

	if (r_C01_w.qt_aceito = 0) then
		tb_nr_sequencia_w(j) := r_C01_w.nr_sequencia;

		if (tb_nr_sequencia_w.count > current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer) then
			forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
				delete	FROM pls_monitor_tiss_cta_val
				where	nr_sequencia = tb_nr_sequencia_w(i);
			commit;

			tb_nr_sequencia_w.delete;
			j := 0;
		else
			j := j + 1;
		end if;
	end if;
end loop;

if (tb_nr_sequencia_w.count > 0) then
	forall i in tb_nr_sequencia_w.first .. tb_nr_sequencia_w.last
		delete	FROM pls_monitor_tiss_cta_val
		where	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
