SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.desconsidera_itens_pac_a500 ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ie_tipo_item_p text) AS $body$
DECLARE


tb_seq_w	pls_util_cta_pck.t_number_table;

C01 CURSOR FOR
	SELECT 	b.nr_sequencia
	from   	pls_monitor_tiss_cta_val a,
			pls_monitor_tiss_proc_val b,
			pls_conta c
	where  	a.nr_seq_Lote_monitor = nr_seq_lote_p
	and    	a.nr_sequencia = b.nr_seq_cta_val
	and    	c.nr_sequencia = a.nr_seq_conta
	and    	c.ie_origem_conta = 'A'
	and exists (
			   SELECT 1
			   from ptu_nota_servico x
			   where x.nr_seq_conta_proc = b.nr_seq_conta_proc
			   and ie_pacote = 'S'
			);

C02 CURSOR FOR 	
	SELECT 	b.nr_sequencia
	from   	pls_monitor_tiss_cta_val a,
			pls_monitor_tiss_mat_val b,
			pls_conta c
	where  	a.nr_seq_Lote_monitor = nr_seq_lote_p
	and    	a.nr_sequencia = b.nr_seq_cta_val
	and    	c.nr_sequencia = a.nr_seq_conta
	and    	c.ie_origem_conta = 'A'
	and exists (
			   SELECT 1
			   from ptu_nota_servico x
			   where x.nr_seq_conta_mat = b.nr_seq_conta_mat
			   and ie_pacote = 'S'
			);	


BEGIN

	if (ie_tipo_item_p = 'P') then
		open c01;
		loop
			tb_seq_w.delete;

			fetch c01 bulk collect into tb_seq_w
			limit pls_util_pck.qt_registro_transacao_w;

			exit when tb_seq_w.count = 0;

			forall i in tb_seq_w.first..tb_seq_w.last
				delete from pls_monitor_tiss_proc_val
				where nr_sequencia = tb_seq_w(i);

			commit;
		end loop;
		close c01;
	else
		open c02;
		loop
			tb_seq_w.delete;

			fetch c02 bulk collect into tb_seq_w
			limit pls_util_pck.qt_registro_transacao_w;

			exit when tb_seq_w.count = 0;

			forall i in tb_seq_w.first..tb_seq_w.last
				delete from pls_monitor_tiss_mat_val
				where nr_sequencia = tb_seq_w(i);

			commit;
		end loop;
		close c02;
	end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
