-- Rotina para a gerao das contas no lote de alterao



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.gerar_contas_envio_ans_alt ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_processo_w	pls_monitor_tempo_lote.nr_sequencia%type;


BEGIN

nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Selecionando contas para o lote de alterao', 'I', nm_usuario_p, nr_seq_processo_w);
-- Seleciona as contas para serem enviadas como altero com base nos filtros criados pelo usurio.  utilizada a mesma estrutura de dados do lote de exclusao por isto da mesma procedure

CALL pls_gerencia_envio_ans_pck.gerar_contas_exclusao(nr_seq_lote_p, nm_usuario_p, cd_estabelecimento_p);
nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);

nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Definindo fornecimento direto', 'I', nm_usuario_p, nr_seq_processo_w);
-- Seleciona as contas para serem enviadas como excluso com base nos filtros criados pelo usurio.

CALL pls_gerencia_envio_ans_pck.definir_fornec_direto_exclusao(nr_seq_lote_p, nm_usuario_p, cd_estabelecimento_p);
nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);

nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Populando dados adicionais das contas selecionadas', 'I', nm_usuario_p, nr_seq_processo_w);
-- Alimenta as informaes adicionais no alimentadas na gerao da conta, nesta etapa ser pego diretamente do ltimo envio de apresentadao da conta.

CALL pls_gerencia_envio_ans_pck.gerar_val_adic_conta_alt(nr_seq_lote_p, nm_usuario_p);
nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);

END;

