-- Rotina para a gerao das contas no lote de excluso



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.gerar_contas_envio_ans_exc ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_processo_w	pls_monitor_tempo_lote.nr_sequencia%type;


BEGIN

nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Selecionando contas para o lote de excluso', 'I', nm_usuario_p, nr_seq_processo_w);
-- Seleciona as contas para serem enviadas como excluso com base nos filtros criados pelo usurio.

CALL pls_gerencia_envio_ans_pck.gerar_contas_exclusao(nr_seq_lote_p, nm_usuario_p, cd_estabelecimento_p);
nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);
					
			
nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Retirando contas que no possuem envios integrados na ANS', 'I', nm_usuario_p, nr_seq_processo_w);
-- Retira as contas que nunca tiveram ao menos um envio aceito na ANS. Adicionado antes do update dos dados adicionais por questo de performance.

--pls_gerencia_envio_ans_pck.deletar_contas_nao_aceitas_exc(nr_seq_lote_p);

nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);

nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Populando dados adicionais das contas selecionadas', 'I', nm_usuario_p, nr_seq_processo_w);
-- Alimenta as informaes adicionais no alimentadas na gerao da conta, nesta etapa ser pego diretamente do ltimo envio de apresentadao da conta.

CALL pls_gerencia_envio_ans_pck.gerar_val_adic_conta_exc(nr_seq_lote_p, nm_usuario_p);
nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);

END;

$body$
