-- Alimenta a composio dos pacotes no lote de excluso



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.gerar_pacote_lote_exc ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


tb_cd_material_w	pls_util_cta_pck.t_varchar2_table_20;
tb_cd_procedimento_w	pls_util_cta_pck.t_varchar2_table_20;
tb_cd_tabela_ref_w	pls_util_cta_pck.t_varchar2_table_20;
tb_ie_origem_proced_w	pls_util_cta_pck.t_varchar2_table_5;
tb_nr_seq_material_w	pls_util_cta_pck.t_number_table;
tb_qt_item_w		pls_util_cta_pck.t_number_table;

C00 CURSOR(nr_seq_lote_pc	pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT	b.nr_sequencia nr_seq_proc_val,
		b.nr_seq_cta_tiss_proc
	from	pls_monitor_tiss_cta_val a,
		pls_monitor_tiss_proc_val b
	where	a.nr_sequencia = b.nr_seq_cta_val
	and	a.nr_seq_lote_monitor = nr_seq_lote_pc;

C01 CURSOR(nr_seq_cta_tiss_proc_pc	pls_monitor_tiss_proc.nr_sequencia%type) FOR
	SELECT	c.cd_material,
		c.cd_procedimento,
		c.cd_tabela_ref,
		c.ie_origem_proced,
		c.nr_seq_material,
		c.qt_item
	from	pls_monitor_tiss_item_pac c
	where	c.nr_seq_cta_tiss_proc = nr_seq_cta_tiss_proc_pc
	and C.cd_tabela_ref <> '00';

BEGIN
for r_C00_w in C00(nr_seq_lote_p) loop

	open C01(r_C00_w.nr_seq_cta_tiss_proc);
	loop
		tb_cd_material_w.delete;
		tb_cd_procedimento_w.delete;
		tb_cd_tabela_ref_w.delete;
		tb_ie_origem_proced_w.delete;
		tb_nr_seq_material_w.delete;
		tb_qt_item_w.delete;

		fetch C01 bulk collect into 	tb_cd_material_w, tb_cd_procedimento_w, tb_cd_tabela_ref_w,
						tb_ie_origem_proced_w, tb_nr_seq_material_w, tb_qt_item_w
		limit current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer;

		exit when tb_cd_material_w.count = 0;

		forall i in tb_cd_material_w.first .. tb_cd_material_w.last
			insert	into	pls_moni_tiss_item_pac_val(	nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
					nm_usuario, nm_usuario_nrec, cd_material,
					cd_procedimento, cd_tabela_ref, ie_origem_proced,
					nr_seq_cta_val_proc, nr_seq_material, qt_item)
			values (	nextval('pls_moni_tiss_item_pac_val_seq'), clock_timestamp(), clock_timestamp(),
					nm_usuario_p, nm_usuario_p, tb_cd_material_w(i),
					tb_cd_procedimento_w(i), tb_cd_tabela_ref_w(i), tb_ie_origem_proced_w(i),
					r_C00_w.nr_seq_proc_val, tb_nr_seq_material_w(i), tb_qt_item_w(i));
		commit;
	end loop;

	if (C01%ISOPEN) then
		close C01;
	end if;
end loop;

END;

$body$
