-- Gera os procedimentos no lote de excluso



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.gerar_procs_envio_ans_exc ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
