CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.gerar_vinculo_conta_retorno ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_seq_cta_val_w		pls_util_cta_pck.t_number_table;
tb_seq_cta_alt_w		pls_util_cta_pck.t_number_table;

-- na hora de criar o vnculo para a conta, o proc e o mat devem ser nulos

c_item_data CURSOR(	nr_seq_lote_pc	pls_monitor_tiss_lote.nr_sequencia%type,
			dt_inicio_pc	pls_monitor_tiss_lote.dt_mes_competencia%type,
			dt_fim_pc	pls_monitor_tiss_lote.dt_mes_competencia%type) FOR
	SELECT	e.nr_sequencia nr_seq_cta_val,
		max(z.nr_sequencia) nr_seq_cta_alt
	from	pls_monitor_tiss_cta_val e,
		pls_monitor_tiss_alt 	z
	where	e.nr_seq_lote_monitor = nr_seq_lote_pc
	and	e.ie_conta_atualizada = 'S'
	and 	z.dt_evento between dt_inicio_pc and dt_fim_pc
	and	z.ie_status in ('P', 'N') -- P = Pendente / N = Negado pela ANS
	and	z.nr_seq_conta = e.nr_seq_conta
	and	coalesce(z.nr_seq_conta_proc::text, '') = ''
	and 	coalesce(z.nr_seq_conta_mat::text, '') = ''
	group by e.nr_sequencia;


BEGIN

open c_item_data(nr_seq_lote_p, current_setting('pls_gerencia_envio_ans_pck.dt_mes_competencia_ini_w')::pls_monitor_tiss_lote.dt_mes_competencia%type, current_setting('pls_gerencia_envio_ans_pck.dt_mes_competencia_fim_w')::pls_monitor_tiss_lote.dt_mes_competencia%type);
loop
	tb_seq_cta_val_w.delete;
	tb_seq_cta_alt_w.delete;

	fetch c_item_data bulk collect into tb_seq_cta_val_w, tb_seq_cta_alt_w
	limit current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer;

	exit when tb_seq_cta_val_w.count = 0;

	forall i in tb_seq_cta_val_w.first .. tb_seq_cta_val_w.last
		insert into pls_monitor_tiss_alt_guia(
			nr_sequencia, nr_seq_cta_alt,
			nr_seq_lote_monitor, nr_seq_cta_val,
			dt_atualizacao, nm_usuario,
			dt_atualizacao_nrec, nm_usuario_nrec
		) values (
			nextval('pls_monitor_tiss_alt_guia_seq'), tb_seq_cta_alt_w(i),
			nr_seq_lote_p, tb_seq_cta_val_w(i),
			clock_timestamp(), nm_usuario_p,
			clock_timestamp(), nm_usuario_p);
	commit;
end loop;
close c_item_data;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
