CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.gerencia_alt_item_conta ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_processo_w	pls_monitor_tempo_lote.nr_sequencia%type;

BEGIN

nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Selecionando contas que sero excludas e reenviadas', 'I', nm_usuario_p, nr_seq_processo_w);
-- grava na tabela temporria pls_cta_reenv_tmp as contas que sero excludas e reenviadas

CALL pls_gerencia_envio_ans_pck.sel_cta_excluir_reenviar(	nr_seq_lote_p, cd_estabelecimento_p);
nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);

nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Gerando novas chaves para contas que sero excludas e reenviadas', 'I', nm_usuario_p, nr_seq_processo_w);
-- gera nova chave na pls_moni_tiss_chave_guia para gerar a excluso

CALL pls_gerencia_envio_ans_pck.cria_chave_exclusao_cta(	nm_usuario_p);
nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);

nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	nr_seq_lote_p, 'Gerando contas que sero atualizadas para incluso', 'I', nm_usuario_p, nr_seq_processo_w);
-- setar as contas que esto sendo enviadas novamente com Tipo de registro 1 - Incluso

CALL pls_gerencia_envio_ans_pck.atualizar_contas_inclusao(	nr_seq_lote_p, nm_usuario_p);
nr_seq_processo_w := pls_gerencia_envio_ans_pck.grava_log_tempo_processo(	null, null, 'F', null, nr_seq_processo_w);

END;

$body$
