SET client_encoding TO 'UTF8';


--Procedure responsvel por inserir as procedimentos nas tabelas utilizadas na gerao do XML



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.inserir_diaria_arquivo_xml ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type) AS $body$
DECLARE


tb_nr_seq_w		pls_util_cta_pck.t_number_table;
tb_qt_diaria_uti_w	pls_util_cta_pck.t_number_table;
tb_qt_diaria_acomp_w	pls_util_cta_pck.t_number_table;

-- Carrega a quantidade total de Dirias UTI e Dirias Acompanhante

C01 CURSOR( nr_seq_lote_pc		pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT 	c.nr_sequencia,
		(SELECT	sum(qt_liberado)
		 from	pls_monitor_tiss_proc
		 where	nr_seq_guia_monitor = c.nr_sequencia
		 and	ie_tipo_diaria = 'DU') qt_diaria_uti,
		(select	sum(qt_liberado)
		 from	pls_monitor_tiss_proc
		 where	nr_seq_guia_monitor = c.nr_sequencia
		 and	ie_tipo_diaria = 'DA') qt_diaria_acomp
	from	pls_monitor_tiss_guia c
	where	c.nr_seq_lote_monitor = nr_seq_lote_pc;


BEGIN

open C01(nr_seq_lote_p);
loop
	tb_nr_seq_w.delete;
	tb_qt_diaria_uti_w.delete;
	tb_qt_diaria_acomp_w.delete;

	fetch C01 bulk collect into tb_nr_seq_w, tb_qt_diaria_uti_w, tb_qt_diaria_acomp_w
	limit current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer;

	exit when tb_nr_seq_w.count = 0;

	forall i in tb_nr_seq_w.first .. tb_nr_seq_w.last

		update	pls_monitor_tiss_guia set
			nr_diarias_acompanhante = tb_qt_diaria_uti_w(i),
			nr_diarias_uti = tb_qt_diaria_acomp_w(i)
		where	nr_sequencia = tb_nr_seq_w(i);

	commit;
end loop;
close C01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerencia_envio_ans_pck.inserir_diaria_arquivo_xml ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type) FROM PUBLIC;
