SET client_encoding TO 'UTF8';


--Procedure responsvel por inserir os pacotes nas tabelas utilizadas na gerao do XML



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.inserir_pacote_arquivo_xml ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type ) AS $body$
DECLARE


tb_nr_seq_proc_tiss_w	pls_util_cta_pck.t_number_table;
tb_cd_material_w	pls_util_cta_pck.t_varchar2_table_15;
tb_nr_seq_material_w	pls_util_cta_pck.t_number_table;
tb_ie_origem_proced_w	pls_util_cta_pck.t_number_table;
tb_cd_procedimento_w	pls_util_cta_pck.t_number_table;
tb_cd_tabela_ref_w	pls_util_cta_pck.t_varchar2_table_2;
tb_qt_item_w		pls_util_cta_pck.t_number_table;

C01 CURSOR( nr_seq_lote_pc		pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT 	f.nr_sequencia,
		z.cd_material,
		z.nr_seq_material,
		z.ie_origem_proced,
		z.cd_procedimento,
		z.cd_tabela_ref,
		z.qt_item
	from	pls_monitor_tiss_guia c,
		pls_monitor_tiss_cta_val b,
		pls_monitor_tiss_proc_val a,
		pls_monitor_tiss_proc f,
		pls_moni_tiss_item_pac_val z
	where	c.nr_seq_lote_monitor = nr_seq_lote_pc
	--and	c.ie_tipo_registro in ('1', '2')

	and	b.nr_seq_lote_monitor = c.nr_seq_lote_monitor
	and	b.nr_seq_conta = c.nr_seq_conta
	and	a.nr_seq_cta_val = b.nr_sequencia
	and	(a.nr_seq_pacote IS NOT NULL AND a.nr_seq_pacote::text <> '')
	and	f.nr_seq_guia_monitor = c.nr_sequencia
	and	f.nr_seq_conta = a.nr_seq_conta
	and	f.nr_seq_conta_proc = a.nr_seq_conta_proc
	and	z.nr_seq_cta_val_proc = a.nr_sequencia;


BEGIN

open C01(nr_seq_lote_p);
loop
	tb_nr_seq_proc_tiss_w.delete;
	tb_cd_material_w.delete;
	tb_nr_seq_material_w.delete;
	tb_ie_origem_proced_w.delete;
	tb_cd_procedimento_w.delete;
	tb_cd_tabela_ref_w.delete;
	tb_qt_item_w.delete;

	fetch C01 bulk collect into 	tb_nr_seq_proc_tiss_w, tb_cd_material_w, tb_nr_seq_material_w,
					tb_ie_origem_proced_w, tb_cd_procedimento_w, tb_cd_tabela_ref_w,
					tb_qt_item_w
	limit current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer;

	exit when tb_nr_seq_proc_tiss_w.count = 0;

	forall i in tb_nr_seq_proc_tiss_w.first .. tb_nr_seq_proc_tiss_w.last

		insert into pls_monitor_tiss_item_pac(
			nr_sequencia, cd_material, cd_procedimento,
			cd_tabela_ref, dt_atualizacao, dt_atualizacao_nrec,
			ie_origem_proced, nm_usuario, nm_usuario_nrec,
			nr_seq_cta_tiss_proc, nr_seq_material, qt_item
		) values (
			nextval('pls_monitor_tiss_item_pac_seq'), tb_cd_material_w(i), tb_cd_procedimento_w(i),
			tb_cd_tabela_ref_w(i), clock_timestamp(), clock_timestamp(),
			tb_ie_origem_proced_w(i), nm_usuario_p, nm_usuario_p,
			tb_nr_seq_proc_tiss_w(i), tb_nr_seq_material_w(i), tb_qt_item_w(i));
		commit;
end loop;
close C01;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
