--Procedure para limpar as inconsistncias geradas na conta de validao



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.limpar_inconsistencias ( tb_nr_seq_cta_val_p pls_util_cta_pck.t_number_table, nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type) AS $body$
BEGIN

if ( tb_nr_seq_cta_val_p.count > 0 ) then
	forall i in tb_nr_seq_cta_val_p.first ..tb_nr_seq_cta_val_p.last

		delete 	from pls_monitor_tiss_inc_val
		where 	nr_seq_cta_val = tb_nr_seq_cta_val_p(i);

	commit;
end if;

delete	FROM pls_monitor_tiss_inc_val
where	nr_seq_lote_monitor = nr_seq_lote_p;

commit;

END;

$body$
