--Function para retornar o cdigo tabela conforme verso do TISS, necessrio para enviar cdigo tabela 63 quando for grupo de procedimento



CREATE OR REPLACE FUNCTION pls_gerencia_envio_ans_pck.obter_cod_tabela_envio_xml ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, cd_tabela_ref_p pls_monitor_tiss_proc_val.cd_tabela_ref%type, cd_grupo_proc_p pls_monitor_tiss_proc_val.cd_grupo_proc%type, cd_versao_tiss_p pls_monitor_tiss_lote.cd_versao_tiss%type, cd_material_tuss_p pls_monitor_tiss_mat_val.cd_material_tuss%type) RETURNS varchar AS $body$
DECLARE


cd_tabela_ref_w		pls_monitor_tiss_proc_val.cd_tabela_ref%type;


BEGIN
cd_tabela_ref_w := cd_tabela_ref_p;

--Verifica se a verso do TISS  igual ou maior que 3.02.01 tambm verifica se existe grupo gerado e se  diferente de 000 (este cdigo  usado somente para controle interno)

if (cd_grupo_proc_p IS NOT NULL AND cd_grupo_proc_p::text <> '') and (cd_grupo_proc_p <> '000') then
	cd_tabela_ref_w := '63';
end if;

return cd_tabela_ref_w;

END;

