--Function para retornar os dados do beneficirio da conta



CREATE OR REPLACE FUNCTION pls_gerencia_envio_ans_pck.obter_dados_beneficiario ( cd_pessoa_fisica_p pessoa_fisica.cd_pessoa_fisica%type, nr_seq_contrato_p pls_contrato.nr_sequencia%type) RETURNS DADOS_BENEFICIARIO AS $body$
DECLARE


dados_beneficiario_w	dados_beneficiario;
ie_tipo_endereco_w	pls_regra_endereco_sib.ie_tipo_endereco%type;
cd_cgc_estipulante_w	pls_contrato.cd_cgc_estipulante%type;


BEGIN

select	a.dt_nascimento,
	a.ie_sexo,
	(select max(p.cd_municipio_ibge)
	 from	compl_pessoa_fisica p
	 where	p.cd_pessoa_fisica 	= a.cd_pessoa_fisica
	 and	p.ie_tipo_complemento 	= 1)
into STRICT	dados_beneficiario_w.dt_nascimento,
	dados_beneficiario_w.ie_sexo,
	dados_beneficiario_w.cd_municipio_benef
from	pessoa_fisica 	a
where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;

--Caso no tiver no endereo residencial, busca no endereo comercial

if (coalesce(dados_beneficiario_w.cd_municipio_benef::text, '') = '') then
	select	max(p.cd_municipio_ibge)
	into STRICT	dados_beneficiario_w.cd_municipio_benef
	from	compl_pessoa_fisica p
	where	p.cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and	p.ie_tipo_complemento 	= 2;
end if;

--Tratamento para buscar a regra do municipio tambm da regra do SIB do endereo do PJ

if (coalesce(dados_beneficiario_w.cd_municipio_benef::text, '') = '') and (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	max(ie_tipo_endereco)
	into STRICT	ie_tipo_endereco_w
	from	pls_regra_endereco_sib
	where	nr_seq_contrato	= nr_seq_contrato_p  LIMIT 1;

	if (ie_tipo_endereco_w	= 'RE') then
		select	cd_cgc_estipulante
		into STRICT	cd_cgc_estipulante_w
		from	pls_contrato
		where	nr_sequencia	= nr_seq_contrato_p;

		if (cd_cgc_estipulante_w IS NOT NULL AND cd_cgc_estipulante_w::text <> '') then
			select	cd_municipio_ibge
			into STRICT	dados_beneficiario_w.cd_municipio_benef
			from	pessoa_juridica
			where	cd_cgc		= cd_cgc_estipulante_w;
		end if;
	end if;
end if;


return	dados_beneficiario_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
