CREATE OR REPLACE FUNCTION pls_gerencia_envio_ans_pck.obter_dados_primeiro_envio ( nr_seq_conta_p pls_conta.nr_sequencia%type) RETURNS PLS_MONITOR_TISS_GUIA.NR_SEQUENCIA%TYPE AS $body$
DECLARE


nr_seq_guia_w		pls_monitor_tiss_guia.nr_sequencia%type;


BEGIN

select	max(b.nr_sequencia)
into STRICT	nr_seq_guia_w
from	pls_monitor_tiss_lote a,
	pls_monitor_tiss_guia b
where	a.nr_sequencia = b.nr_seq_lote_monitor
and	coalesce(a.ie_tipo_lote,'0') <> '1'
and	b.nr_seq_conta = nr_seq_conta_p
and	b.ie_tipo_evento_alt = 'FC'
and	exists (	SELECT	1
		from	pls_mon_tiss_guia_ret_act x
		where	x.nr_seq_guia_monitor = b.nr_sequencia);

if (coalesce(nr_seq_guia_w::text, '') = '') then
	select	max(b.nr_sequencia)
	into STRICT	nr_seq_guia_w
	from	pls_monitor_tiss_lote a,
		pls_monitor_tiss_guia b
	where	a.nr_sequencia = b.nr_seq_lote_monitor
	and	coalesce(a.ie_tipo_lote,'0') <> '1'
	and	b.nr_seq_conta = nr_seq_conta_p
	and	coalesce(b.dt_pagamento_previsto::text, '') = ''
	and	exists (	SELECT	1
			from	pls_mon_tiss_guia_ret_act x
			where	x.nr_seq_guia_monitor = b.nr_sequencia);
end if;

return nr_seq_guia_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
