CREATE OR REPLACE FUNCTION pls_gerencia_envio_ans_pck.obter_informacao_alter_date ( nr_seq_conta_p pls_conta.nr_sequencia%type, ie_opcao_p text, dt_valor_atual_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;
qt_registro_alt_w	integer;
ds_campo_w		varchar(255);
ds_select_w		varchar(255);


BEGIN

select	count(1)
into STRICT	qt_registro_alt_w
from	pls_monitor_tiss_aj_cta
where	nr_seq_conta = nr_seq_conta_p;

if (qt_registro_alt_w = 0) then
	dt_retorno_w := dt_valor_atual_p;
else
	if (ie_opcao_p = 'DS') then

		ds_campo_w := 'dt_solicitacao';

	elsif (ie_opcao_p = 'DA') then

		ds_campo_w := 'dt_autorizacao';

	elsif (ie_opcao_p = 'DIF') then

		ds_campo_w := 'dt_inicio_faturamento';

	elsif (ie_opcao_p = 'DFF') then

		ds_campo_w := 'dt_fim_internacao';

	elsif (ie_opcao_p = 'DP') then

		ds_campo_w := 'dt_protocolo';
		
	end if;

	ds_select_w := 'select max(' || ds_campo_w || ') from pls_monitor_tiss_aj_cta where nr_seq_conta = :nr_seq_conta';

	EXECUTE ds_select_w into STRICT dt_retorno_w using nr_seq_conta_p;

	if (coalesce(dt_retorno_w::text, '') = '') then
		dt_retorno_w := dt_valor_atual_p;
	end if;
end if;

return dt_retorno_w;

END;

$body$
