SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_gerencia_envio_ans_pck.obter_informacao_alter_varchar ( nr_seq_conta_p pls_conta.nr_sequencia%type, ie_opcao_p text, ds_valor_atual_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(20);
qt_registro_alt_w	integer;
ds_campo_w		varchar(255);
ds_select_w		varchar(255);


BEGIN

if (ie_opcao_p not in ('DO', 'DN')) then
	select	count(1)
	into STRICT	qt_registro_alt_w
	from	pls_monitor_tiss_aj_cta
	where	nr_seq_conta = nr_seq_conta_p;

	if (qt_registro_alt_w = 0) then
		ds_retorno_w := ds_valor_atual_p;
	else
		if (ie_opcao_p = 'TC') then

			ds_campo_w := 'ie_tipo_consulta';

		elsif (ie_opcao_p = 'IRN') then

			ds_campo_w := 'ie_recem_nasc';

		elsif (ie_opcao_p = 'IA') then

			ds_campo_w := 'ie_indicacao_acidente';

		elsif (ie_opcao_p = 'CA') then

			ds_campo_w := 'ie_carater_atendimento';

		elsif (ie_opcao_p = 'TI') then

			ds_campo_w := 'ie_tipo_internacao';

		elsif (ie_opcao_p = 'RI') then

			ds_campo_w := 'ie_regime_internacao';

		elsif (ie_opcao_p = 'CD') then

			ds_campo_w := 'cd_doenca';

		elsif (ie_opcao_p = 'TA') then

			ds_campo_w := 'ie_tipo_atendimento';

		elsif (ie_opcao_p = 'TF') then

			ds_campo_w := 'ie_tipo_faturamento';

		elsif (ie_opcao_p = 'ME') then

			ds_campo_w := 'ie_motivo_encerramento';
		
		elsif (ie_opcao_p = 'CNES') then

			ds_campo_w := 'cd_cnes';
		
		end if;

		ds_select_w := 'select max(' || ds_campo_w || ') from pls_monitor_tiss_aj_cta where nr_seq_conta = :nr_seq_conta';

		EXECUTE ds_select_w into STRICT ds_retorno_w using nr_seq_conta_p;

		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w := ds_valor_atual_p;
		end if;
	end if;
else
	if (ie_opcao_p = 'DO') then

		select	count(1)
		into STRICT	qt_registro_alt_w
		from	pls_monitor_tiss_aj_obito
		where	nr_seq_conta = nr_seq_conta_p;

		if (qt_registro_alt_w = 0) then
			ds_retorno_w := ds_valor_atual_p;
		else
			select	max(replace(nr_declaracao,'-',''))
			into STRICT	ds_retorno_w
			from	pls_monitor_tiss_aj_obito
			where	nr_seq_conta = nr_seq_conta_p;
		end if;
	elsif (ie_opcao_p = 'DN') then
		select	count(1)
		into STRICT	qt_registro_alt_w
		from	pls_monitor_tiss_aj_nasc
		where	nr_seq_conta = nr_seq_conta_p;

		if (qt_registro_alt_w = 0) then
			ds_retorno_w := ds_valor_atual_p;
		else
			select	max(replace(nr_declaracao,'-',''))
			into STRICT	ds_retorno_w
			from	pls_monitor_tiss_aj_nasc
			where	nr_seq_conta = nr_seq_conta_p;
		end if;
	end if;
end if;

return ds_retorno_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
