CREATE OR REPLACE FUNCTION pls_gerencia_envio_ans_pck.obter_se_envia_pagamento ( ie_tipo_envio_p pls_monitor_tiss_alt.ie_tipo_evento%type, nr_seq_conta_p pls_conta.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS varchar AS $body$
DECLARE


ie_gera_pgto_w			varchar(1) := 'S';
ie_rede_contratada_w		varchar(1) := 'S';


BEGIN
