CREATE OR REPLACE FUNCTION pls_gerencia_envio_ans_pck.obter_se_proc_mat_ret ( nr_seq_guia_ret_p pls_monitor_tiss_guia_ret.nr_sequencia%type, cd_iem_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_guia_monitor_w		pls_monitor_tiss_guia.nr_sequencia%type;
qt_item_w			integer;
ds_retorno_w			varchar(2);


BEGIN
ds_retorno_w := null;

select	nr_seq_guia_monitor
into STRICT	nr_seq_guia_monitor_w
from	pls_monitor_tiss_guia_ret
where	nr_sequencia = nr_seq_guia_ret_p;

if (nr_seq_guia_monitor_w IS NOT NULL AND nr_seq_guia_monitor_w::text <> '') then
	select	count(1)
	into STRICT	qt_item_w
	from	pls_monitor_tiss_proc
	where	nr_seq_guia_monitor = nr_seq_guia_ret_p
	and	cd_procedimento = cd_iem_p;

	if (qt_item_w > 0) then
		ds_retorno_w := 'P';
	else
		select	count(1)
		into STRICT	qt_item_w
		from	pls_monitor_tiss_mat
		where	nr_seq_guia_monitor = nr_seq_guia_ret_p
		and	cd_material = cd_iem_p;

		if (qt_item_w > 0) then
			ds_retorno_w := 'M';
		end if;
	end if;
end if;

return ds_retorno_w;

END;

