--Function para obter o tipo de diria, este campo ser utilizado para obter a quantidade de Dirias UTI e Acompanhante



CREATE OR REPLACE FUNCTION pls_gerencia_envio_ans_pck.obter_tipo_diaria ( cd_procedimento_p pls_monitor_tiss_proc_val.cd_procedimento%type, ie_origem_proced_p pls_monitor_tiss_proc_val.ie_origem_proced%type, dt_procedimento_p pls_conta_proc.dt_procedimento_referencia%type) RETURNS varchar AS $body$
DECLARE


ie_tipo_diaria_w	pls_monitor_tiss_proc_val.ie_tipo_diaria%type;


BEGIN

select 	max(b.ie_tipo_diaria)
into STRICT	ie_tipo_diaria_w
from	pls_regra_diaria 	a,
	pls_regra_diaria_proc 	b
where	b.nr_seq_regra_diaria	= a.nr_sequencia
and	b.cd_procedimento 	= cd_procedimento_p
and	b.ie_origem_proced	= ie_origem_proced_p
and	a.dt_inicio_vigencia 	<= fim_dia(dt_procedimento_p)
and (coalesce(a.dt_fim_vigencia::text, '') = '' or a.dt_fim_vigencia > trunc(dt_procedimento_p));

return ie_tipo_diaria_w;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
