SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.sel_cta_excluir_reenviar ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_lote_ultimo_envio_w	pls_monitor_tiss_lote.nr_sequencia%type;
qt_registro_w			integer;
tb_seq_conta_w			pls_util_cta_pck.t_number_table;
tb_seq_ult_lote_w		pls_util_cta_pck.t_number_table;
qt_registro_ins_w		integer;

-- lista todas as contas que j foram enviadas para a ANS pelo menos uma vez e foram aceitas

c01 CURSOR( nr_seq_lote_pc	pls_monitor_tiss_lote.nr_sequencia%type	) FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_conta,
		(SELECT	count(1)
		from   	pls_monitor_tiss_guia b,
			pls_moni_tiss_chave_guia c
		where	b.nr_seq_conta = a.nr_seq_conta
		and	b.nr_seq_lote_monitor != a.nr_seq_lote_monitor
		and	b.ie_tipo_registro = '1'
		and	c.nr_sequencia = b.nr_seq_chave_guia
		and	c.ie_status = 'E') qt_chave,
		(select	count(1)
		from   	pls_monitor_tiss_alt_guia d,
			pls_monitor_tiss_alt z
		where	d.nr_seq_guia_monitor = a.nr_sequencia
		and	z.nr_sequencia = d.nr_seq_cta_alt
		and	z.ie_tipo_evento = 'FC') qt_alt
	from   	pls_monitor_tiss_guia a
	where  	a.nr_seq_lote_monitor = nr_seq_lote_pc;
	-- somente evento de fechamento de conta (conta foi fechada, enviada, reaberta, alterada e agora precisa ser enviada novamente)

BEGIN

-- limpa a tabela temporria somente para evitar problemas

EXECUTE 'truncate table pls_cta_reenv_tmp';
qt_registro_ins_w := 0;

for r_c01_w in c01(nr_seq_lote_p) loop
	-- somente evento de fechamento de conta (conta foi fechada, enviada, reaberta, alterada e agora precisa ser enviada novamente)

	if (r_c01_w.qt_chave > 0) and (r_c01_w.qt_alt > 0) then

		-- obtm o nmero do ltimo lote em que a conta foi enviada e aceita pela ANS (evento de insero)

		nr_seq_lote_ultimo_envio_w := pls_gerencia_envio_ans_pck.obter_lote_ultimo_envio_cta(	r_c01_w.nr_seq_conta, nr_seq_lote_p, cd_estabelecimento_p);

		-- busca diferenas entre o que foi enviado para a ANS no ltimo envio com o que existe para ser enviado hoje

		select	count(1)
		into STRICT	qt_registro_w
		from	(
				(SELECT	b.nr_seq_conta,
					b.ie_origem_proced,
					b.cd_procedimento,
					null nr_seq_material
				from 	pls_monitor_tiss_guia a,
					pls_monitor_tiss_proc b
				where	a.nr_seq_lote_monitor = nr_seq_lote_ultimo_envio_w
				and	a.nr_seq_conta = r_c01_w.nr_seq_conta
				and 	b.nr_seq_guia_monitor = a.nr_sequencia
				
union all

				SELECT	b.nr_seq_conta,
					null ie_origem_proced,
					null cd_procedimento,
					b.nr_seq_material
				from 	pls_monitor_tiss_guia a,
					pls_monitor_tiss_mat b
				where 	a.nr_seq_lote_monitor = nr_seq_lote_ultimo_envio_w
				and	a.nr_seq_conta = r_c01_w.nr_seq_conta
				and 	b.nr_seq_guia_monitor = a.nr_sequencia)
				EXCEPT(select	b.nr_seq_conta,
					b.ie_origem_proced,
					b.cd_procedimento,
					null nr_seq_material
				from 	pls_monitor_tiss_guia a,
					pls_monitor_tiss_proc b
				where	a.nr_seq_lote_monitor = nr_seq_lote_p
				and	a.nr_seq_conta = r_c01_w.nr_seq_conta
				and 	b.nr_seq_guia_monitor = a.nr_sequencia
				
union all

				select	b.nr_seq_conta,
					null ie_origem_proced,
					null cd_procedimento,
					b.nr_seq_material
				from 	pls_monitor_tiss_guia a,
					pls_monitor_tiss_mat b
				where 	a.nr_seq_lote_monitor = nr_seq_lote_p
				and	a.nr_seq_conta = r_c01_w.nr_seq_conta
				and 	b.nr_seq_guia_monitor = a.nr_sequencia)
			) alias3;

		-- se no encontrou diferena de registros inverte a busca

		if (qt_registro_w = 0) then

			select	count(1)
			into STRICT	qt_registro_w
			from	(
					(SELECT	b.nr_seq_conta,
						b.ie_origem_proced,
						b.cd_procedimento,
						null nr_seq_material
					from 	pls_monitor_tiss_guia a,
						pls_monitor_tiss_proc b
					where	a.nr_seq_lote_monitor = nr_seq_lote_p
					and	a.nr_seq_conta = r_c01_w.nr_seq_conta
					and 	b.nr_seq_guia_monitor = a.nr_sequencia
					
union all

					SELECT	b.nr_seq_conta,
						null ie_origem_proced,
						null cd_procedimento,
						b.nr_seq_material
					from 	pls_monitor_tiss_guia a,
						pls_monitor_tiss_mat b
					where 	a.nr_seq_lote_monitor = nr_seq_lote_p
					and	a.nr_seq_conta = r_c01_w.nr_seq_conta
					and 	b.nr_seq_guia_monitor = a.nr_sequencia)
					EXCEPT(select	b.nr_seq_conta,
						b.ie_origem_proced,
						b.cd_procedimento,
						null nr_seq_material
					from 	pls_monitor_tiss_guia a,
						pls_monitor_tiss_proc b
					where	a.nr_seq_lote_monitor = nr_seq_lote_ultimo_envio_w
					and	a.nr_seq_conta = r_c01_w.nr_seq_conta
					and 	b.nr_seq_guia_monitor = a.nr_sequencia
					
union all

					select	b.nr_seq_conta,
						null ie_origem_proced,
						null cd_procedimento,
						b.nr_seq_material
					from 	pls_monitor_tiss_guia a,
						pls_monitor_tiss_mat b
					where 	a.nr_seq_lote_monitor = nr_seq_lote_ultimo_envio_w
					and	a.nr_seq_conta = r_c01_w.nr_seq_conta
					and 	b.nr_seq_guia_monitor = a.nr_sequencia)
				) alias4;
		end if;

		-- se foi detectada diferena dos registros atuais com os que foram enviados para a ANS

		-- registra na tabela pls_cta_reenv_tmp que a conta precisa ser excluda e reenviada

		if (qt_registro_w > 0) then

			tb_seq_conta_w(qt_registro_ins_w) := r_c01_w.nr_seq_conta;
			tb_seq_ult_lote_w(qt_registro_ins_w) := nr_seq_lote_ultimo_envio_w;

			if ( qt_registro_ins_w >= current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer ) then

				-- manda para o banco

				CALL pls_gerencia_envio_ans_pck.inserir_cta_excluir(tb_seq_conta_w, tb_seq_ult_lote_w);

				qt_registro_ins_w := 0;
				tb_seq_conta_w.delete;
				tb_seq_ult_lote_w.delete;
			else
				qt_registro_ins_w := qt_registro_ins_w + 1;
			end if;
		end if;
	end if;
end loop;

-- se sobrou algum registro manda para o banco

CALL pls_gerencia_envio_ans_pck.inserir_cta_excluir(tb_seq_conta_w, tb_seq_ult_lote_w);
tb_seq_conta_w.delete;
tb_seq_ult_lote_w.delete;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerencia_envio_ans_pck.sel_cta_excluir_reenviar ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;
