SET client_encoding TO 'UTF8';


-- Busca os materiais para inserir no lote de excluso



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.selecionar_mat_lote_exc ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


tb_cd_cgc_fornecedor_w		pls_util_cta_pck.t_varchar2_table_20;
tb_cd_grupo_proc_w		pls_util_cta_pck.t_varchar2_table_20;
tb_cd_material_w		pls_util_cta_pck.t_varchar2_table_20;
tb_cd_tabela_ref_w		pls_util_cta_pck.t_varchar2_table_20;
tb_cd_material_tuss_w		pls_util_cta_pck.t_varchar2_table_20;
tb_ie_tipo_despesa_w		pls_util_cta_pck.t_varchar2_table_5;
tb_nr_seq_alt_w			pls_util_cta_pck.t_number_table;
tb_nr_seq_prest_fornec_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_conta_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_conta_mat_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_material_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_pacote_w		pls_util_cta_pck.t_number_table;
tb_qt_liberado_w		pls_util_cta_pck.t_number_table;
tb_qt_material_w		pls_util_cta_pck.t_number_table;
tb_vl_liberado_w		pls_util_cta_pck.t_number_table;
tb_vl_material_w		pls_util_cta_pck.t_number_table;
tb_vl_coparticipacao_w		pls_util_cta_pck.t_number_table;
tb_vl_pago_fornecedor_w		pls_util_cta_pck.t_number_table;
tb_cd_unidade_medida_w	pls_util_cta_pck.t_varchar2_table_5;


C01 CURSOR(nr_seq_lote_pc	pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT	a.nr_seq_monitor_guia,
		a.nr_sequencia nr_seq_cta_val,
		a.nr_seq_conta
	from	pls_monitor_tiss_cta_val a
	where	a.nr_seq_lote_monitor = nr_seq_lote_pc;

C02 CURSOR(	nr_seq_monitor_guia_pc	pls_monitor_tiss_guia.nr_sequencia%type,
		nr_seq_conta_pc		pls_conta.nr_sequencia%type) FOR
	SELECT	a.cd_cgc_fornecedor,
		a.cd_grupo_proc,
		a.cd_material,
		a.cd_tabela_ref,
		a.ie_tipo_despesa,
		a.nr_seq_conta,
		a.nr_seq_conta_mat,
		a.nr_seq_material,
		a.nr_seq_pacote,
		a.qt_liberado,
		a.qt_material,
		coalesce(a.vl_liberado,0),
		coalesce(a.vl_material,0),
		coalesce(a.vl_pago_fornecedor,0),
		pls_monitor_obter_cd_mat_tuss(a.nr_seq_material),
		(SELECT	max(x.nr_sequencia)
		 from	pls_monitor_tiss_alt x
		 where	x.nr_seq_conta = nr_seq_conta_pc
		 and	x.nr_seq_conta_mat = a.nr_seq_conta_mat),
		(select	max(z.nr_seq_prest_fornec)
		 from	pls_conta_mat z
		 where	z.nr_sequencia = a.nr_seq_conta_mat),
		coalesce(a.vl_coparticipacao,0),
		cd_unidade_medida
	from	pls_monitor_tiss_mat a
	where	a.nr_seq_guia_monitor = nr_seq_monitor_guia_pc;

BEGIN
for r_C01_w in C01(nr_seq_lote_p) loop

	open C02(r_C01_w.nr_seq_monitor_guia, r_C01_w.nr_seq_conta);
	loop
		tb_cd_cgc_fornecedor_w.delete;
		tb_cd_grupo_proc_w.delete;
		tb_cd_material_w.delete;
		tb_cd_tabela_ref_w.delete;
		tb_ie_tipo_despesa_w.delete;
		tb_nr_seq_conta_w.delete;
		tb_nr_seq_conta_mat_w.delete;
		tb_nr_seq_material_w.delete;
		tb_nr_seq_pacote_w.delete;
		tb_qt_liberado_w.delete;
		tb_qt_material_w.delete;
		tb_vl_liberado_w.delete;
		tb_vl_material_w.delete;
		tb_vl_pago_fornecedor_w.delete;
		tb_cd_material_tuss_w.delete;
		tb_nr_seq_alt_w.delete;
		tb_nr_seq_prest_fornec_w.delete;
		tb_vl_coparticipacao_w.delete;
		tb_cd_unidade_medida_w.delete;

		fetch C02 bulk collect into 	tb_cd_cgc_fornecedor_w, tb_cd_grupo_proc_w, tb_cd_material_w,
						tb_cd_tabela_ref_w, tb_ie_tipo_despesa_w, tb_nr_seq_conta_w,
						tb_nr_seq_conta_mat_w, tb_nr_seq_material_w, tb_nr_seq_pacote_w,
						tb_qt_liberado_w, tb_qt_material_w, tb_vl_liberado_w,
						tb_vl_material_w, tb_vl_pago_fornecedor_w, tb_cd_material_tuss_w,
						tb_nr_seq_alt_w, tb_nr_seq_prest_fornec_w, tb_vl_coparticipacao_w,
						tb_cd_unidade_medida_w
		limit current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer;

		exit when tb_cd_cgc_fornecedor_w.count = 0;

		forall i in tb_cd_cgc_fornecedor_w.first .. tb_cd_cgc_fornecedor_w.last
			insert	into	pls_monitor_tiss_mat_val(	nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
					nm_usuario, nm_usuario_nrec, cd_cgc_fornecedor,
					cd_grupo_proc, cd_material, cd_material_tuss,
					cd_tabela_ref, dt_evento, ie_glosa,
					ie_item_atualizado, ie_tipo_despesa, ie_tipo_evento,
					nr_seq_conta, nr_seq_conta_mat, nr_seq_conta_rec,
					nr_seq_cta_alt, nr_seq_cta_val, nr_seq_material,
					nr_seq_mat_rec, nr_seq_pacote, nr_seq_prest_fornec,
					qt_liberado, qt_material, vl_glosa,
					vl_liberado, vl_material, vl_pago_fornecedor,
					vl_coparticipacao, cd_unidade_medida)
			values	(	nextval('pls_monitor_tiss_mat_val_seq'), clock_timestamp(), clock_timestamp(),
					nm_usuario_p, nm_usuario_p, tb_cd_cgc_fornecedor_w(i),
					tb_cd_grupo_proc_w(i), tb_cd_material_w(i), tb_cd_material_tuss_w(i),
					tb_cd_tabela_ref_w(i), null, null,
					'S', tb_ie_tipo_despesa_w(i), 'E',
					tb_nr_seq_conta_w(i), tb_nr_seq_conta_mat_w(i), null,
					tb_nr_seq_alt_w(i), r_C01_w.nr_seq_cta_val, tb_nr_seq_material_w(i),
					null, tb_nr_seq_pacote_w(i), tb_nr_seq_prest_fornec_w(i),
					tb_qt_liberado_w(i), tb_qt_material_w(i), (tb_vl_material_w(i) - tb_vl_liberado_w(i)),
					tb_vl_liberado_w(i), tb_vl_material_w(i), tb_vl_pago_fornecedor_w(i),
					tb_vl_coparticipacao_w(i), tb_cd_unidade_medida_w(i));
		commit;
	end loop;

	if (C02%ISOPEN) then
		close C02;
	end if;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerencia_envio_ans_pck.selecionar_mat_lote_exc ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) FROM PUBLIC;
