-- Seleciona os procedimentos conforme primeiro envio



CREATE OR REPLACE PROCEDURE pls_gerencia_envio_ans_pck.selecionar_proc_lote_exc ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


tb_cd_dente_w			pls_util_cta_pck.t_varchar2_table_20;
tb_cd_face_dente_w		pls_util_cta_pck.t_varchar2_table_20;
tb_cd_grupo_proc_w		pls_util_cta_pck.t_varchar2_table_20;
tb_cd_procedimento_w		pls_util_cta_pck.t_varchar2_table_20;
tb_cd_regiao_boca_w		pls_util_cta_pck.t_varchar2_table_20;
tb_cd_tabela_ref_w		pls_util_cta_pck.t_varchar2_table_20;
tb_ie_origem_proced_w		pls_util_cta_pck.t_varchar2_table_5;
tb_ie_tipo_despesa_w		pls_util_cta_pck.t_varchar2_table_5;
tb_ie_tipo_diaria_w		pls_util_cta_pck.t_varchar2_table_5;
tb_nr_seq_conta_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_conta_proc_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_guia_monitor_w	pls_util_cta_pck.t_number_table;
tb_nr_seq_pacote_w		pls_util_cta_pck.t_number_table;
tb_qt_liberado_w		pls_util_cta_pck.t_number_table;
tb_qt_procedimento_w		pls_util_cta_pck.t_number_table;
tb_vl_liberado_w		pls_util_cta_pck.t_number_table;
tb_vl_procedimento_w		pls_util_cta_pck.t_number_table;
tb_vl_coparticipacao_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_alt_w			pls_util_cta_pck.t_number_table;
tb_nr_seq_cta_tiss_proc_w	pls_util_cta_pck.t_number_table;

C01 CURSOR(nr_seq_lote_pc	pls_monitor_tiss_lote.nr_sequencia%type) FOR
	SELECT	a.nr_seq_monitor_guia,
		a.nr_sequencia nr_seq_cta_val,
		a.nr_seq_conta
	from	pls_monitor_tiss_cta_val a
	where	a.nr_seq_lote_monitor = nr_seq_lote_pc;

C02 CURSOR(	nr_seq_guia_monitor_pc	pls_monitor_tiss_guia.nr_sequencia%type,
		nr_seq_conta_pc		pls_conta.nr_sequencia%type) FOR
	SELECT	a.cd_dente,
		a.cd_face_dente,
		a.cd_grupo_proc,
		a.cd_procedimento,
		a.cd_regiao_boca,
		a.cd_tabela_ref,
		a.ie_origem_proced,
		a.ie_tipo_despesa,
		a.ie_tipo_diaria,
		a.nr_seq_conta,
		a.nr_seq_conta_proc,
		a.nr_seq_guia_monitor,
		a.nr_seq_pacote,
		a.qt_liberado,
		a.qt_procedimento,
		coalesce(a.vl_liberado,0),
		coalesce(a.vl_procedimento,0),
		(SELECT	max(x.nr_sequencia)
		 from	pls_monitor_tiss_alt x
		 where	x.nr_seq_conta = nr_seq_conta_pc
		 and	x.nr_seq_conta_proc = a.nr_seq_conta_proc) nr_seq_alt,
		a.nr_sequencia,
		coalesce(a.vl_coparticipacao,0)
	from	pls_monitor_tiss_proc a
	where	a.nr_seq_guia_monitor = nr_seq_guia_monitor_pc;

BEGIN
for r_C01_w in C01(nr_seq_lote_p) loop

	open C02(r_C01_w.nr_seq_monitor_guia, r_C01_w.nr_seq_conta);
	loop
		tb_cd_dente_w.delete;
		tb_cd_face_dente_w.delete;
		tb_cd_grupo_proc_w.delete;
		tb_cd_procedimento_w.delete;
		tb_cd_regiao_boca_w.delete;
		tb_cd_tabela_ref_w.delete;
		tb_ie_origem_proced_w.delete;
		tb_ie_tipo_despesa_w.delete;
		tb_ie_tipo_diaria_w.delete;
		tb_nr_seq_conta_w.delete;
		tb_nr_seq_conta_proc_w.delete;
		tb_nr_seq_guia_monitor_w.delete;
		tb_nr_seq_pacote_w.delete;
		tb_qt_liberado_w.delete;
		tb_qt_procedimento_w.delete;
		tb_vl_liberado_w.delete;
		tb_vl_procedimento_w.delete;
		tb_nr_seq_alt_w.delete;
		tb_nr_seq_cta_tiss_proc_w.delete;
		tb_vl_coparticipacao_w.delete;

		fetch C02 bulk collect into 	tb_cd_dente_w, tb_cd_face_dente_w, tb_cd_grupo_proc_w,
						tb_cd_procedimento_w, tb_cd_regiao_boca_w, tb_cd_tabela_ref_w,
						tb_ie_origem_proced_w, tb_ie_tipo_despesa_w, tb_ie_tipo_diaria_w,
						tb_nr_seq_conta_w, tb_nr_seq_conta_proc_w, tb_nr_seq_guia_monitor_w,
						tb_nr_seq_pacote_w, tb_qt_liberado_w, tb_qt_procedimento_w,
						tb_vl_liberado_w, tb_vl_procedimento_w, tb_nr_seq_alt_w,
						tb_nr_seq_cta_tiss_proc_w, tb_vl_coparticipacao_w
		limit current_setting('pls_gerencia_envio_ans_pck.qt_registro_transacao_w')::integer;

		exit when tb_cd_dente_w.count = 0;

		forall i in tb_cd_dente_w.first .. tb_cd_dente_w.last
			insert 	into 	pls_monitor_tiss_proc_val(	nr_sequencia, dt_atualizacao, dt_atualizacao_nrec,
					nm_usuario, nm_usuario_nrec, cd_dente,
					cd_face_dente, cd_grupo_proc, cd_procedimento,
					cd_regiao_boca, cd_tabela_ref, dt_evento,
					ie_glosa, ie_item_atualizado, ie_origem_proced,
					ie_tipo_despesa, ie_tipo_diaria, ie_tipo_evento,
					nr_seq_conta, nr_seq_conta_proc, nr_seq_conta_rec,
					nr_seq_cta_alt, nr_seq_cta_val, nr_seq_pacote,
					nr_seq_proc_rec, qt_liberado, qt_procedimento,
					vl_glosa, vl_liberado, vl_procedimento,
					nr_seq_cta_tiss_proc, vl_coparticipacao)
			values	(	nextval('pls_monitor_tiss_proc_val_seq'), clock_timestamp(), clock_timestamp(),
					nm_usuario_p, nm_usuario_p, tb_cd_dente_w(i),
					tb_cd_face_dente_w(i), tb_cd_grupo_proc_w(i), tb_cd_procedimento_w(i),
					tb_cd_regiao_boca_w(i), tb_cd_tabela_ref_w(i), null,
					null, 'S', tb_ie_origem_proced_w(i),
					tb_ie_tipo_despesa_w(i), tb_ie_tipo_diaria_w(i), 'E',
					tb_nr_seq_conta_w(i), tb_nr_seq_conta_proc_w(i), null,
					tb_nr_seq_alt_w(i), r_C01_w.nr_seq_cta_val, tb_nr_seq_pacote_w(i),
					null, tb_qt_liberado_w(i), tb_qt_procedimento_w(i),
					(tb_vl_procedimento_w(i) - tb_vl_liberado_w(i)), tb_vl_liberado_w(i), tb_vl_procedimento_w(i),
					tb_nr_seq_cta_tiss_proc_w(i), tb_vl_coparticipacao_w(i));
		commit;
	end loop;

	if (C02%ISOPEN) then
		close C02;
	end if;
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_gerencia_envio_ans_pck.selecionar_proc_lote_exc ( nr_seq_lote_p pls_monitor_tiss_lote.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) FROM PUBLIC;
