SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION pls_gerencia_envio_ans_pck.tratar_codigo_guia_envio ( cd_guia_p text) RETURNS varchar AS $body$
DECLARE


cd_guia_ajustado_w	  	varchar(255);
ds_novo_w			varchar(255)	:= 'aaaaaeeeiooouucAAAAAEEEIOOOUUC';
ds_padrao_w			varchar(255);
ds_acento_w			varchar(255);


BEGIN

if (cd_guia_p IS NOT NULL AND cd_guia_p::text <> '') then

	select ds_sql
	into STRICT	ds_padrao_w
	from	dic_objeto
	where	nr_sequencia = 1106730;

	EXECUTE ds_padrao_w
	into STRICT ds_acento_w;

	cd_guia_ajustado_w	:= regexp_replace(cd_guia_p, '[^a-zA-Z--+0-9]', '');
	cd_guia_ajustado_w	:= Translate(cd_guia_ajustado_w,ds_acento_w,ds_novo_w);

	if (coalesce(length(regexp_replace(cd_guia_p, '[^a-zA-Z--+1-9]', '')),0) = 0) then
		cd_guia_ajustado_w := '';
	end if;
end if;

return cd_guia_ajustado_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
