CREATE OR REPLACE PROCEDURE pls_gerencia_senha_web_pck.pls_alterar_senha_usuario_web ( ds_senha_p text, nr_seq_usuario_p bigint, ie_tipo_login_p text, qt_senha_diferente_p bigint, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, ds_senha_sha256_p INOUT text, ds_tec_p INOUT text) AS $body$
DECLARE
																				

ds_tec_w				varchar(15);
ds_senha_sha256_w		varchar(64);								

BEGIN

select  replace(replace(dbms_random.string('P', 15), chr(39), ''), ';', '')
into STRICT	ds_tec_w
;

ds_senha_sha256_w := obter_sha2(upper(ds_senha_p) || ds_tec_w, 256);		

if (ie_tipo_login_p = 'P') then

	update  pls_usuario_web
	set    	ds_senha = ds_senha_sha256_w,
			ds_tec = ds_tec_w,
			dt_alteracao_senha = clock_timestamp(),
			dt_atualizacao = clock_timestamp(),
			qt_tentativa_acesso = 0,
			nm_usuario = nm_usuario_p
	where  	nr_sequencia = nr_seq_usuario_p;
	
elsif (ie_tipo_login_p = 'B') then

	update  pls_segurado_web
	set    	ds_senha = ds_senha_sha256_w,
			ds_tec = ds_tec_w,
			dt_alteracao_senha = clock_timestamp(),
			dt_atualizacao = clock_timestamp(),
			qt_tentativa_acesso = 0,
			nm_usuario = nm_usuario_p
	where  	nr_sequencia = nr_seq_usuario_p;
	
elsif (ie_tipo_login_p = 'E') then

	update  pls_estipulante_web
	set    	ds_senha = ds_senha_sha256_w,
			ds_tec = ds_tec_w,
			dt_alteracao_senha = clock_timestamp(),
			dt_atualizacao = clock_timestamp(),
			qt_tentativa_acesso = 0,
			nm_usuario = nm_usuario_p
	where  	nr_sequencia = nr_seq_usuario_p;

elsif (ie_tipo_login_p = 'GC') then

	update  pls_grupo_contrato_web
	set    	ds_senha = ds_senha_sha256_w,
			ds_tec = ds_tec_w,
			dt_alteracao_senha = clock_timestamp(),
			dt_atualizacao = clock_timestamp(),
			qt_tentativa_acesso = 0,
			nm_usuario = nm_usuario_p
	where  	nr_sequencia = nr_seq_usuario_p;
	
elsif (ie_tipo_login_p = 'PA') then

	update  pls_proposta_adesao_web
	set    	ds_senha = ds_senha_sha256_w,
			ds_tec = ds_tec_w,
			dt_atualizacao = clock_timestamp(),
			nm_usuario = nm_usuario_p
	where  	nr_sequencia = nr_seq_usuario_p;
	
elsif (ie_tipo_login_p = 'CO') then

	update  pls_cooperado_web
	set    	ds_senha = ds_senha_sha256_w,
			ds_tec = ds_tec_w,
			dt_alteracao_senha = clock_timestamp(),
			dt_atualizacao = clock_timestamp(),
			qt_tentativa_acesso = 0,
			nm_usuario = nm_usuario_p
	where  	nr_sequencia = nr_seq_usuario_p;
	
end if;

if (qt_senha_diferente_p > 0) then
	CALL pls_gerencia_senha_web_pck.pls_controla_registros_usuario(ds_senha_sha256_w, ds_tec_w, nr_seq_usuario_p, ie_tipo_login_p, qt_senha_diferente_p, nm_usuario_p, cd_estabelecimento_p);
end if;

commit;

ds_senha_sha256_p := ds_senha_sha256_w;
ds_tec_p := ds_tec_w;

END;

$body$
