CREATE OR REPLACE FUNCTION pls_gerenciar_reembolso_pck.montar_select_padrao_reemb ( ie_select_p text, dados_restricao_p pls_gerenciar_reembolso_pck.dados_restricao_select) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Finalidade: Cria o select padrao de acordo com a aplicaao da regra sendo verificada.  
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


ds_select_w		varchar(4000);
ds_select_proc_w	varchar(4000);
ds_select_mat_w		varchar(4000);


BEGIN

ds_select_proc_w :=	'select	a.nr_seq_conta	nr_seq_conta, 		' || pls_util_pck.enter_w ||
			'	a.nr_sequencia	nr_seq_conta_proc, 	' || pls_util_pck.enter_w ||
			'	null	nr_seq_conta_mat 		' || pls_util_pck.enter_w ||
			'from	pls_conta_proc_v a			' || pls_util_pck.enter_w ||
			'where	1 = 1 					' || pls_util_pck.enter_w ||
			'and	a.nr_seq_regra_acao_reemb is null	' || pls_util_pck.enter_w ||
			dados_restricao_p.ds_restricao_proc;
			
ds_select_mat_w	:= 	'select	a.nr_seq_conta	nr_seq_conta, 		' || pls_util_pck.enter_w ||
			'	null	nr_seq_conta_proc, 		' || pls_util_pck.enter_w ||
			'	a.nr_sequencia	nr_seq_conta_mat 	' || pls_util_pck.enter_w ||
			'from	pls_conta_mat_v	 a			' || pls_util_pck.enter_w ||
			'where	1 = 1 					' || pls_util_pck.enter_w ||
			'and	a.nr_seq_regra_acao_reemb is null	' || pls_util_pck.enter_w ||
			dados_restricao_p.ds_restricao_mat;

if (ie_select_p = 'P') then
	ds_select_w	:= ds_select_proc_w;
elsif (ie_select_p = 'M') then
	ds_select_w	:= ds_select_mat_w;
else
	ds_select_w	:= 	ds_select_proc_w || pls_util_pck.enter_w ||
				'union all ' || pls_util_pck.enter_w ||
				ds_select_mat_w;
end if;			

return	ds_select_w;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
