CREATE OR REPLACE FUNCTION pls_gerenciar_reembolso_pck.obter_restricao_atendimento ( dados_filtro_atend_p pls_gerenciar_reembolso_pck.dados_filtro_atend, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Montar as restricoes e binds dos campos de filtro de atendimento.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


dados_restricao_w	pls_gerenciar_reembolso_pck.dados_select;

ds_filtro_w		varchar(2000);
ds_alias_w		varchar(6);


BEGIN

-- Inicializar o retorno

dados_restricao_w.ds_restricao	:= null;
ds_alias_w  	:= 'a';

ds_filtro_w 	:= null;

-- Tipo atendimento - NR_SEQ_TIPO_ATENDIMENTO

if (dados_filtro_atend_p.nr_seq_tipo_atendimento IS NOT NULL AND dados_filtro_atend_p.nr_seq_tipo_atendimento::text <> '') then
	ds_filtro_w := 	ds_filtro_w || pls_util_pck.enter_w || 
			'and ' || ds_alias_w || '.nr_seq_tipo_atendimento = :nr_seq_tipo_atendimento ';
	
	valor_bind_p := sql_pck.bind_variable(':nr_seq_tipo_atendimento', dados_filtro_atend_p.nr_seq_tipo_atendimento, valor_bind_p);
end if;

-- Tipo guia - IE_TIPO_GUIA

if (dados_filtro_atend_p.ie_tipo_guia IS NOT NULL AND dados_filtro_atend_p.ie_tipo_guia::text <> '') then
	ds_filtro_w := 	ds_filtro_w || pls_util_pck.enter_w ||
			'and ' || ds_alias_w || '.ie_tipo_guia = :ie_tipo_guia ';
	
	valor_bind_p := sql_pck.bind_variable(':ie_tipo_guia', dados_filtro_atend_p.ie_tipo_guia, valor_bind_p);
