-- Tipo acomodacao - NR_SEQ_TIPO_ACOMOD_CONTA

if (dados_filtro_atend_p.nr_seq_tipo_acomod_conta IS NOT NULL AND dados_filtro_atend_p.nr_seq_tipo_acomod_conta::text <> '') then
	ds_filtro_w := 	ds_filtro_w || pls_util_pck.enter_w ||
