end if;	

if (dados_filtro_atend_p.nr_seq_mot_reembolso IS NOT NULL AND dados_filtro_atend_p.nr_seq_mot_reembolso::text <> '') then
	ds_filtro_w := 	ds_filtro_w || pls_util_pck.enter_w ||
