CREATE OR REPLACE FUNCTION pls_gerenciar_reembolso_pck.obter_restricao_material ( dados_filtro_mat_p pls_gerenciar_reembolso_pck.dados_filtro_mat, valor_bind_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE
			
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Montar as restricoes e binds dos campos de filtro de material.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */


dados_restricao_w	pls_gerenciar_reembolso_pck.dados_select;

ds_filtro_w		varchar(2000);
ds_alias_w		varchar(6);


BEGIN

-- Inicializar o retorno

dados_restricao_w.ds_restricao	:= null;
ds_filtro_w 	:= null;
ds_alias_w  	:= 'a';

-- Tipo despesa - IE_TIPO_DESPESA_MAT

if (dados_filtro_mat_p.ie_tipo_despesa_mat IS NOT NULL AND dados_filtro_mat_p.ie_tipo_despesa_mat::text <> '') then
	ds_filtro_w := 	ds_filtro_w || pls_util_pck.enter_w || 
			'and ' || ds_alias_w || '.ie_tipo_despesa = :ie_tipo_despesa_mat ';
	
	valor_bind_p := sql_pck.bind_variable(':ie_tipo_despesa_mat', dados_filtro_mat_p.ie_tipo_despesa_mat, valor_bind_p);
end if;

--tipo transacao

if (dados_filtro_mat_p.ie_tipo_transacao IS NOT NULL AND dados_filtro_mat_p.ie_tipo_transacao::text <> '') then
	ds_filtro_w := 	ds_filtro_w || pls_util_pck.enter_w ||
			' and exists(select 1 from pls_conta_mat_regra cta where '|| pls_util_pck.enter_w || 
			' cta.nr_sequencia = '||ds_alias_w||'.nr_sequencia and cta.ie_tp_transacao = :ie_tipo_transacao )';
	
	valor_bind_p := sql_pck.bind_variable(':ie_tipo_transacao', dados_filtro_mat_p.ie_tipo_transacao, valor_bind_p);
end if;

if (ds_filtro_w IS NOT NULL AND ds_filtro_w::text <> '') then
	dados_restricao_w.ds_restricao := ds_filtro_w;
end if;

return;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON FUNCTION pls_gerenciar_reembolso_pck.obter_restricao_material ( dados_filtro_mat_p pls_gerenciar_reembolso_pck.dados_filtro_mat, valor_bind_p INOUT sql_pck.t_dado_bind) FROM PUBLIC;
