CREATE OR REPLACE PROCEDURE pls_gerenciar_reembolso_pck.pls_reemb_limpa_regra ( nr_seq_conta_p pls_conta.nr_sequencia%type) AS $body$
DECLARE

					
tb_sequencia_w	pls_util_cta_pck.t_number_table;	

C01 CURSOR(	nr_seq_conta_pc			pls_conta.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia nr_seq_conta_proc
	from	pls_conta_proc_v a
	where	a.nr_seq_conta = nr_seq_conta_pc;
	
C02 CURSOR(	nr_seq_conta_pc			pls_conta.nr_sequencia%type) FOR
	SELECT	a.nr_sequencia nr_seq_conta_mat
	from	pls_conta_mat_v a
	where	a.nr_seq_conta = nr_seq_conta_pc;


BEGIN

-- Procedimento

open C01(nr_seq_conta_p);
loop	
	-- Limpa os registros da lista para que nao seja processado o mesmo registro mais de uma vez.

	tb_sequencia_w.delete;
	
	-- Executa o fetch de um numero determinado de linhas do cursor na lista

	fetch C01 bulk collect into tb_sequencia_w limit pls_util_cta_pck.qt_registro_transacao_w;

	-- Quando o cursor nao tiver mais linhas entao sai do loop

	exit when tb_sequencia_w.count = 0;
				
	--Faz update dos registros

	forall i in tb_sequencia_w.first..tb_sequencia_w.last
		update	pls_conta_proc
		set	nr_seq_regra_acao_reemb	 = NULL		
		where	nr_sequencia	= tb_sequencia_w(i);
	commit;
end loop;
close C01;

-- Material

open C02(nr_seq_conta_p);
loop	
	-- Limpa os registros da lista para que nao seja processado o mesmo registro mais de uma vez.

	tb_sequencia_w.delete;
	
	-- Executa o fetch de um numero determinado de linhas do cursor na lista

	fetch C02 bulk collect into tb_sequencia_w limit pls_util_cta_pck.qt_registro_transacao_w;

	-- Quando o cursor nao tiver mais linhas entao sai do loop

	exit when tb_sequencia_w.count = 0;
				
	--Faz update dos registros

	forall i in tb_sequencia_w.first..tb_sequencia_w.last
		update	pls_conta_mat
		set	nr_seq_regra_acao_reemb	 = NULL		
		where	nr_sequencia	= tb_sequencia_w(i);
	commit;
end loop;
close C02;

tb_sequencia_w.delete;

END;

