CREATE OR REPLACE FUNCTION pls_gerenciar_reembolso_pck.retorna_sql_cursor_prot ( dados_regra_aviso_p pls_gerenciar_reembolso_pck.dados_regra_aviso, bind_sql_valor_p INOUT sql_pck.t_dado_bind) AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Gerar o sql do cursor que busca os protocolos conforme a regra
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[X]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
-------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
Alteracoes:
-------------------------------------------------------------------------------------------------------------------

++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */

sql_cursor_w		varchar(4000);

BEGIN
sql_cursor_w :=	'select	count(1) qt_protocolos '|| pls_util_pck.enter_w ||
		'from	pls_protocolo_conta '|| pls_util_pck.enter_w || 
		'where	ie_tipo_protocolo	= ''R'' '|| pls_util_pck.enter_w;
if (dados_regra_aviso_p.ie_status IS NOT NULL AND dados_regra_aviso_p.ie_status::text <> '') then
	sql_cursor_w :=	sql_cursor_w ||	' and	ie_status		= :ie_status '|| pls_util_pck.enter_w;
	bind_sql_valor_p := sql_pck.bind_variable(	':ie_status', dados_regra_aviso_p.ie_status, bind_sql_valor_p);
end if;

sql_cursor_w :=	sql_cursor_w ||	 'and	((dt_protocolo + :qt_dias_limite) - (sysdate)) between 0 and :qt_dias '||  pls_util_pck.enter_w;
bind_sql_valor_p := sql_pck.bind_variable(	':qt_dias_limite', retorna_qt_dias_prazo_limite, bind_sql_valor_p);
bind_sql_valor_p := sql_pck.bind_variable(	':qt_dias', dados_regra_aviso_p.qt_dia_antecedente, bind_sql_valor_p);

return;

END;

$body$
