CREATE OR REPLACE FUNCTION pls_imp_farmacia_pck.obter_dados_prot_conta ( nr_seq_imp_prot_conta_p pls_imp_prot_conta.nr_sequencia%type, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
  ie_opcao_p
  P  - Sequencial protocolo da conta
*/

ds_retorno_w  varchar(1000);

BEGIN

ds_retorno_w := '';

if (nr_seq_imp_prot_conta_p IS NOT NULL AND nr_seq_imp_prot_conta_p::text <> '') then

  if (ie_opcao_p = 'P') then

    select  max(a.nr_seq_protocolo)
    into STRICT  ds_retorno_w
    from  pls_imp_cta    a
    where  a.nr_seq_prot_imp  = nr_seq_imp_prot_conta_p;
  end if;

end if;

return ds_retorno_w;

END;

$body$
