SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE pls_imp_pre_pagto_pck.conversao_materiais ( nr_seq_prot_gerado_p pls_faturamento_prot.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


nr_seq_material_w	pls_material.nr_sequencia%type;
ie_somente_codigo_w	pls_regra_conv_mat_interc.ie_somente_codigo%type;
ie_tipo_despesa_w	pls_material.ie_tipo_despesa%type;
cd_servico_mat_w	varchar(50);
qt_material_w		integer := 0;
		
C01 CURSOR FOR
	SELECT	a.cd_versao_tiss,
			c.cd_procedimento cd_item,
			c.dt_execucao,
			c.nr_sequencia
	from	pls_faturamento_prot a,
			pls_faturamento_conta b,
			pls_fat_conta_item c
	where	a.nr_sequencia = nr_seq_prot_gerado_p
	and		b.nr_seq_protocolo = a.nr_sequencia
	and		c.nr_seq_conta = b.nr_sequencia
	and 	c.ie_tipo_item = 'OD';
		
BEGIN

for r_c01_w in c01 loop

	nr_seq_material_w := null;
	pls_obter_mat_tiss_vigente( nr_seq_material_w, clock_timestamp(), r_c01_w.cd_item, 'O', 'N', r_c01_w.cd_versao_tiss);
	
	if (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then
		select	count(nr_sequencia)
		into STRICT	qt_material_w
		from	pls_material
		where	nr_sequencia	= nr_seq_material_w;
	end if;
	
	if ( qt_material_w = 0) then
		ptu_obter_material_conversao(	r_c01_w.cd_item,
						null, --nao tem a informacao do tipo tabela ate aqui 
						r_c01_w.dt_execucao,
						'R', 
						'7', 
						null, 
						null,
						nr_seq_material_w,
						cd_servico_mat_w,
						ie_somente_codigo_w );
	end if;	
			
	--Se ocorreu uma conversao, entao determinou que o item e um material.

	if (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then
	
		select max(ie_tipo_despesa)
		into STRICT	ie_tipo_despesa_w
		from	pls_material
		where	nr_sequencia = nr_seq_material_w;
	
		update 	pls_fat_conta_item
		set	ie_tipo_item_conv = 'M',
			ie_tipo_despesa_conv = ie_tipo_despesa_w,
			nr_seq_material_conv = nr_seq_material_w				
		where	nr_sequencia = r_c01_w.nr_sequencia;
			
	end if;
	
end loop;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
