-- Converter a tag para data e hora



CREATE OR REPLACE FUNCTION pls_imp_pre_pagto_pck.converte_data_hora ( data_p text, hora_p text) RETURNS timestamp AS $body$
BEGIN

if (coalesce(data_p,'X') != 'X') and (coalesce(hora_p,'X') != 'X') then
	return to_date(data_p || ' ' || hora_p,'dd/mm/yyyy hh24:mi:ss');
else
	return null;
end if;

END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
